// Formatar resposta
const items = $input.all();

if (items.length === 0) {
  return {
    json: {
      message: 'Nenhuma categoria encontrada',
      categorias: []
    }
  };
}

const receitas = items.filter(i => i.json.tipo === 'receita').map(i => ({
  id: i.json.id,
  nome: i.json.nome,
  descricao: i.json.descricao
}));

const despesas = items.filter(i => i.json.tipo === 'despesa').map(i => ({
  id: i.json.id,
  nome: i.json.nome,
  descricao: i.json.descricao
}));

return {
  json: {
    total: items.length,
    categorias_receita: receitas,
    categorias_despesa: despesas
  }
};
