// Pega todos os itens
const items = $input.all();

// VERIFICA SE H√Å DADOS
if (!items || items.length === 0) {
  return [{
    json: {
      message: "‚ö†Ô∏è Nenhum dado dispon√≠vel para o per√≠odo selecionado.\n\nExecute o workflow completo (desde o in√≠cio) para buscar dados novos do Facebook Ads."
    }
  }];
}

// Extrai os dados do json
const data = items.map(item => item.json);

// Pega o nome do cliente (assume que todos s√£o da mesma conta)
const clientName = data[0]['Account Name'] || 'N/A';

// Calcula o per√≠odo (pega a menor e maior data)
const dates = data.map(item => item.Date).filter(d => d);
const startDate = dates.length > 0 ? dates.sort()[0] : 'N/A';
const endDate = dates.length > 0 ? dates.sort()[dates.length - 1] : 'N/A';

// Calcula totais
const totalSpend = data.reduce((sum, ad) => sum + (parseFloat(ad.Spend) || 0), 0);
const totalImpressions = data.reduce((sum, ad) => sum + (parseInt(ad.Impressions) || 0), 0);
const totalClicks = data.reduce((sum, ad) => sum + (parseInt(ad.Clicks) || 0), 0);
const totalConversas = data.reduce((sum, ad) => sum + (parseInt(ad['Conversas Iniciadas']) || 0), 0);

// Calcula m√©tricas
const custoMensagem = totalConversas > 0 ? totalSpend / totalConversas : 0;
const cpm = totalImpressions > 0 ? (totalSpend / totalImpressions) * 1000 : 0;
const ctr = totalImpressions > 0 ? (totalClicks / totalImpressions) * 100 : 0;
const taxaConversao = totalClicks > 0 ? (totalConversas / totalClicks) * 100 : 0;

// Ordena por spend (maior para menor) e pega TOP 3
const top3 = data
  .sort((a, b) => (parseFloat(b.Spend) || 0) - (parseFloat(a.Spend) || 0))
  .slice(0, 3);

// Monta a mensagem
let message = `Bom dia, pessoal! Report atualizado das campanhas\n`;
message += `CLIENTE: ${clientName}\n\n`;
message += `üìÖ RELAT√ìRIO DI√ÅRIO\n`;
message += `üìÖ Per√≠odo: ${startDate} at√© ${endDate}\n`;
message += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
message += `Valor Investido: $ ${totalSpend.toFixed(2)}\n`;
message += `Conversas Iniciadas: ${totalConversas}\n`;
message += `Custo por mensagem: $ ${custoMensagem.toFixed(2)}\n`;
message += `CPM: $ ${cpm.toFixed(2)}\n`;
message += `CTR: ${ctr.toFixed(2)}%\n`;
message += `Taxa de Convers√£o: ${taxaConversao.toFixed(2)}%\n`;
message += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
message += `TOP 3 ADS POR GASTO:\n`;

// Adiciona TOP 3
top3.forEach((ad, index) => {
  const adName = ad['Ad Name'] || 'N/A';
  const adSpend = parseFloat(ad.Spend) || 0;
  const adConversas = parseInt(ad['Conversas Iniciadas']) || 0;
  const custoPorConv = adConversas > 0 ? adSpend / adConversas : 0;

  message += `${index + 1}. ${adName}\n`;
  message += `   Gasto: $ ${adSpend.toFixed(2)} | Convers√µes: ${adConversas} | Custo/Conv: $${custoPorConv.toFixed(2)}\n`;
});

return [{
  json: {
    message: message
  }
}];

const items = $input.all();

// Remove duplicatas baseado no Ad ID
const uniqueItems = [];
const seenIds = new Set();

for (const item of items) {
  const adId = item.json['Ad ID'];
  if (!seenIds.has(adId)) {
