// Consolidar relatório semanal
const config = $('Config GHL').first().json;
const resumo = $('Resumo Semana').first().json;
const topDespesas = $('Top 5 Despesas').all().filter(i => i.json && i.json.descricao);

// Função para formatar valor
const formatarValor = (valor) => new Intl.NumberFormat('pt-BR', {
  style: 'currency',
  currency: 'BRL'
}).format(Number(valor || 0));

// Calcular saldo
const receitasRealizadas = parseFloat(resumo.receitas_realizadas) || 0;
const despesasRealizadas = parseFloat(resumo.despesas_realizadas) || 0;
const saldoSemana = receitasRealizadas - despesasRealizadas;

const entradasPrevistas = parseFloat(resumo.entradas_previstas) || 0;
const saidasPrevistas = parseFloat(resumo.saidas_previstas) || 0;
const saldoPrevisto = entradasPrevistas - saidasPrevistas;

// Data do relatório
const hoje = new Date();
const semanaAnteriorInicio = new Date(hoje);
semanaAnteriorInicio.setDate(hoje.getDate() - 7);

const formatarDataCurta = (d) => d.toLocaleDateString('pt-BR', {day: '2-digit', month: '2-digit'});

// Montar mensagem
let mensagem = `*RELATÓRIO SEMANAL FINANCEIRO*\n`;
mensagem += `Período: ${formatarDataCurta(semanaAnteriorInicio)} a ${formatarDataCurta(hoje)}\n\n`;

// Resumo da semana
mensagem += `*RESUMO DA SEMANA*\n`;
mensagem += `Receitas: ${formatarValor(receitasRealizadas)} (${resumo.receitas_quitados || 0} recebidos)\n`;
mensagem += `Despesas: ${formatarValor(despesasRealizadas)} (${resumo.despesas_quitados || 0} pagos)\n`;
mensagem += `*Saldo: ${formatarValor(saldoSemana)}* ${saldoSemana >= 0 ? '(positivo)' : '(negativo)'}\n\n`;

// Top despesas
if (topDespesas.length > 0) {
  mensagem += `*MAIORES GASTOS*\n`;
  topDespesas.slice(0, 5).forEach((item, idx) => {
    const d = item.json;
    mensagem += `${idx + 1}. ${d.descricao}: ${formatarValor(d.valor)}\n`;
  });
  mensagem += `\n`;
}

// Inadimplência
const valorInadimplencia = parseFloat(resumo.valor_inadimplencia) || 0;
if (valorInadimplencia > 0) {
  mensagem += `*INADIMPLÊNCIA*\n`;
  mensagem += `${resumo.qtd_inadimplentes || 0} títulos em atraso\n`;
  mensagem += `Total: *${formatarValor(valorInadimplencia)}*\n\n`;
}

// Previsão
mensagem += `*PRÓXIMOS 7 DIAS*\n`;
mensagem += `Entradas previstas: ${formatarValor(entradasPrevistas)}\n`;
mensagem += `Saídas previstas: ${formatarValor(saidasPrevistas)}\n`;
mensagem += `Saldo previsto: *${formatarValor(saldoPrevisto)}*\n\n`;

mensagem += `---\nMottivme Sales - BPO Financeiro`;

return [{
  json: {
    mensagem,
    admin_contact_id: config.admin_contact_id,
    ghl_api_key: config.ghl_api_key,
    dados: {
      receitas_realizadas: receitasRealizadas,
      despesas_realizadas: despesasRealizadas,
      saldo_semana: saldoSemana,
      inadimplencia: valorInadimplencia,
      entradas_previstas: entradasPrevistas,
      saidas_previstas: saidasPrevistas
    }
  }
}];
