// Validar e preparar dados do parcelamento
const input = $json;

// Campos obrigat√≥rios
const tipo = input.tipo; // 'receita' ou 'despesa'
const descricao = input.descricao;
const valorTotal = parseFloat(input.valor_total);
const numParcelas = parseInt(input.num_parcelas);
const dataPrimeira = input.data_primeira_parcela;

// Valida√ß√µes
const erros = [];

if (!tipo || !['receita', 'despesa'].includes(tipo)) {
  erros.push('Tipo deve ser "receita" ou "despesa"');
}

if (!descricao || descricao.trim().length < 3) {
  erros.push('Descri√ß√£o √© obrigat√≥ria (m√≠nimo 3 caracteres)');
}

if (!valorTotal || valorTotal <= 0) {
  erros.push('Valor total deve ser maior que zero');
}

if (!numParcelas || numParcelas < 2 || numParcelas > 120) {
  erros.push('N√∫mero de parcelas deve ser entre 2 e 120');
}

if (!dataPrimeira) {
  erros.push('Data da primeira parcela √© obrigat√≥ria');
}

if (erros.length > 0) {
  return {
    json: {
      sucesso: false,
      erro: erros.join('; ')
    }
  };
}

// Calcular valor da parcela
const valorParcela = Math.round((valorTotal / numParcelas) * 100) / 100;

return {
  json: {
    sucesso: true,
    tipo,
    descricao: descricao.trim(),
    valor_total: valorTotal,
    num_parcelas: numParcelas,
    valor_parcela: valorParcela,
    data_primeira_parcela: dataPrimeira,
    tipo_entidade: input.tipo_entidade || 'pj',
    categoria_id: input.categoria_id || null,
    cliente_fornecedor_id: input.cliente_fornecedor_id || null,
    observacao: input.observacao || null
  }
};

// Gerar as movimenta√ß√µes de cada parcela direto para fin_movimentacoes
const dados = $json;

const parcelas = [];
let dataBase = new Date(dados.data_primeira_parcela + 'T00:00:00');

for (let i = 1; i <= dados.num_parcelas; i++) {
  const dataVencimento = new Date(dataBase);
  dataVencimento.setMonth(dataVencimento.getMonth() + (i - 1));

  parcelas.push({
    tipo: dados.tipo,
    descricao: dados.descricao,
    valor_previsto: dados.valor_parcela,
    data_vencimento: dataVencimento.toISOString().split('T')[0],
    tipo_entidade: dados.tipo_entidade,
    categoria_id: dados.categoria_id,
    cliente_fornecedor_id: dados.cliente_fornecedor_id,
    parcela_numero: i,
    parcelas_total: dados.num_parcelas,
    observacao: dados.observacao
  });
}

return parcelas.map(p => ({ json: p }));

// Formatar resposta de sucesso
const dadosOriginais = $('Validar Dados').first().json;
const parcelasInseridas = $('Inserir Parcela').all();

const formatarValor = (valor) => new Intl.NumberFormat('pt-BR', {
  style: 'currency',
  currency: 'BRL'
}).format(valor || 0);

const formatarData = (dataStr) => {
  if (!dataStr) return '-';
  const data = new Date(dataStr + 'T00:00:00');
  return data.toLocaleDateString('pt-BR');
};

return {
  json: {
    sucesso: true,
    mensagem: `Parcelamento criado com sucesso!`,
    detalhes: {
      tipo: dadosOriginais.tipo === 'receita' ? 'üí∞ A Receber' : 'üí≥ A Pagar',
      descricao: dadosOriginais.descricao,
      valor_total: formatarValor(dadosOriginais.valor_total),
      parcelas: `${dadosOriginais.num_parcelas}x de ${formatarValor(dadosOriginais.valor_parcela)}`,
      primeira_parcela: formatarData(dadosOriginais.data_primeira_parcela),
      movimentacoes_criadas: parcelasInseridas.length
    }
  }
};

// Retornar erro de valida√ß√£o
return {
  json: {
    sucesso: false,
    erro: $json.erro
  }
};
