parameters.jsCode:
// ============================================
// VALIDADOR DE COMPLETUDE - DADOS ENRIQUECIDOS
// ============================================

const items = $input.all();
const validatedData = [];

for (const item of items) {
  const data = item.json;

  // Fun√ß√£o para verificar se campo existe e n√£o est√° vazio
  function isComplete(value) {
    if (value === null || value === undefined) return false;
    if (typeof value === 'string') return value.trim().length > 0;
    if (typeof value === 'number') return true;
    if (Array.isArray(value)) return value.length > 0;
    if (typeof value === 'object') return Object.keys(value).length > 0;
    return false;
  }

  // Calcular scores por categoria
  const scores = {
    clone_config: 0,
    arquetipo: 0,
    valores: 0,
    linguagem: 0,
    avatares: 0,
    promessa_big_idea: 0,
    proposito_missao_visao: 0,
    diferenciacao: 0,
    ofertas: 0,
    comunicacao: 0,
    trafego: 0
  };

  // 1. Clone Config (peso 10)
  if (isComplete(data.clone_config)) {
    if (data.clone_config.temperature) scores.clone_config += 3;
    if (data.clone_config.formality_level) scores.clone_config += 2;
    if (data.clone_config.prohibited_behaviors?.length > 0) scores.clone_config += 2.5;
    if (data.clone_config.mandatory_behaviors?.length > 0) scores.clone_config += 2.5;
  }

  // 2. Arqu√©tipos (peso 10)
  if (isComplete(data.arquetipo_principal)) scores.arquetipo += 5;
  if (isComplete(data.arquetipo_secundario)) scores.arquetipo += 5;

  // 3. Valores (peso 10)
  if (isComplete(data.valores_core)) {
    if (data.valores_core.core_value) scores.valores += 4;
    if (data.valores_core.supporting_values?.length > 0) scores.valores += 3;
    if (data.valores_core.anti_values?.length > 0) scores.valores += 3;
  }

  // 4. Linguagem (peso 15)
  if (isComplete(data.bordoes)) {
    if (data.bordoes.principais?.length > 0) scores.linguagem += 5;
    if (data.bordoes.estruturas_linguisticas) scores.linguagem += 3;
  }
  if (isComplete(data.vocabulario)) {
    if (data.vocabulario.sempre_usar?.length > 0) scores.linguagem += 4;
    if (data.vocabulario.nunca_usar?.length > 0) scores.linguagem += 3;
  }

  // 5. Avatares (peso 15)
  if (data.avatares?.length > 0) {
    scores.avatares = Math.min(15, data.avatares.length * 5);
  }

  // 6. Promessa + Big Idea (peso 10)
  if (isComplete(data.promessa_central)) scores.promessa_big_idea += 5;
  if (isComplete(data.big_idea)) scores.promessa_big_idea += 5;

  // 7. Prop√≥sito/Miss√£o/Vis√£o (peso 10)
  if (isComplete(data.proposito)) scores.proposito_missao_visao += 3.5;
  if (isComplete(data.missao)) scores.proposito_missao_visao += 3.5;
  if (isComplete(data.visao)) scores.proposito_missao_visao += 3;

  // 8. Diferencia√ß√£o (peso 5)
  if (isComplete(data.diferenciacao)) scores.diferenciacao = 5;

  // 9. Ofertas (peso 5)
  if (data.ofertas) {
    if (data.ofertas.high_ticket?.length > 0) scores.ofertas += 2;
    if (data.ofertas.back_end?.length > 0) scores.ofertas += 1.5;
    if (data.ofertas.front_end?.length > 0) scores.ofertas += 1.5;
  }

  // 10. Comunica√ß√£o (peso 5)
  if (isComplete(data.comunicacao_avancada)) scores.comunicacao = 5;

  // 11. Tr√°fego (peso 5)
  if (isComplete(data.estrategia_trafego)) scores.trafego = 5;

  // Calcular score total
  const scoreTotal = Object.values(scores).reduce((a, b) => a + b, 0);

  // Determinar qualidade
  let qualidade, pode_usar;
  if (scoreTotal >= 90) {
    qualidade = "EXCELENTE";
    pode_usar = ["full_personalization", "all_scenarios"];
  } else if (scoreTotal >= 75) {
    qualidade = "BOM";
    pode_usar = ["personalization", "most_scenarios"];
  } else if (scoreTotal >= 60) {
    qualidade = "ACEIT√ÅVEL";
    pode_usar = ["basic_personalization", "simple_scenarios"];
  } else {
    qualidade = "INSUFICIENTE";
    pode_usar = ["generic_only"];
  }

  // Identificar campos faltantes cr√≠ticos
  const camposFaltantes = [];
  if (scores.clone_config < 8) camposFaltantes.push("clone_config");
  if (scores.arquetipo < 8) camposFaltantes.push("arqu√©tipos");
  if (scores.linguagem < 12) camposFaltantes.push("bord√µes_vocabul√°rio");
  if (scores.avatares < 10) camposFaltantes.push("avatares_detalhados");
  if (scores.promessa_big_idea < 8) camposFaltantes.push("promessa_big_idea");

  // Adicionar valida√ß√£o ao objeto
  validatedData.push({
    json: {
      ...data,
      validation: {
        score_total: Math.round(scoreTotal),
        score_detalhado: scores,
        qualidade: qualidade,
        pode_usar: pode_usar,
        campos_faltantes: camposFaltantes,
        completo: scoreTotal >= 90,
        validated_at: new Date().toISOString()
      }
    }
  });
}

return validatedData;

// ============================================
// FORMATADOR PARA USO EM PROMPTS DE IA
// ============================================

const items = $input.all();
const formattedData = [];

for (const item of items) {
  const data = item.json;

  // ===== CONTEXTO COMPACTO PARA SYSTEM PROMPT =====
  const systemContext = `
<expert_identity>
  <basic>
    <name>${data.expert_name}</name>
    <id>${data.expert_id}</id>
  </basic>

  <voice_calibration>
    <temperature>${data.clone_config?.temperature || 0.3}</temperature>
    <formality>${data.clone_config?.formality_level || 7}</formality>
    <emotional_range>${data.clone_config?.emotional_range || 'Restrito'}</emotional_range>
  </voice_calibration>

  <archetype>
    <primary name="${data.arquetipo_principal?.name}" weight="${data.arquetipo_principal?.percentage}%" />
    <secondary name="${data.arquetipo_secundario?.name}" weight="${data.arquetipo_secundario?.percentage}%" />
  </archetype>

  <core_values>
    ${data.valores_core?.core_value ? `<main>${data.valores_core.core_value.nome}</main>` : ''}
    ${data.valores_core?.anti_values?.map(v => `<reject>${v}</reject>`).join('\n') || ''}
  </core_values>

  <vocabulary>
    <always_use>${data.vocabulario?.sempre_usar?.join(', ') || ''}</always_use>
    <never_use>${data.vocabulario?.nunca_usar?.join(', ') || ''}</never_use>
  </vocabulary>

  <signature_phrases>
    ${data.bordoes?.principais?.map(b => `<phrase context="${b.quando_usar}">${b.frase}</phrase>`).join('\n') || ''}
  </signature_phrases>

  <positioning>
    <promise>${data.promessa_central?.versao_principal || ''}</promise>
    <mechanism>${data.big_idea?.nome_mecanismo || ''}</mechanism>
    <differentiator>${data.diferenciacao?.unico_diferenciador || ''}</differentiator>
  </positioning>
</expert_identity>
`;

  // ===== CONTEXTO DETALHADO PARA USER MESSAGE =====
  const userContext = `
EXPERT: ${data.expert_name}

CALIBRA√á√ÉO DE VOZ:
- Formalidade: ${data.clone_config?.formality_level || 7}/10
- Temperatura: ${data.clone_config?.temperature || 0.3}
- Emojis: ${data.comunicacao_avancada?.uso_emojis?.frequencia || 'Raramente'}
- Provoca√ß√£o: Alto n√≠vel - provoca√ß√£o intelectual

ARQU√âTIPOS:
- Principal: ${data.arquetipo_principal?.name} (${data.arquetipo_principal?.percentage}%)
- Secund√°rio: ${data.arquetiposecundario?.name} (${data.arquetipo_secundario?.percentage}%)

VALORES CORE:
‚úì ${data.valores_core?.core_value?.nome}: ${data.valores_core?.core_value?.manifestacao}
${data.valores_core?.supporting_values?.map(v => `‚úì ${v.nome}: ${v.manifestacao}`).join('\n') || ''}

ANTI-VALORES (NUNCA):
${data.valores_core?.anti_values?.map(v => `‚úó ${v}`).join('\n') || ''}

VOCABUL√ÅRIO:
SEMPRE usar: ${data.vocabulario?.sempre_usar?.slice(0, 10).join(', ') || ''}
NUNCA usar: ${data.vocabulario?.nunca_usar?.slice(0, 10).join(', ') || ''}

BORD√ïES PRINCIPAIS:
${data.bordoes?.principais?.map((b, i) => `${i + 1}. "${b.frase}" - Use quando: ${b.quando_usar}`).join('\n') || ''}

PROMESSA CENTRAL:
"${data.promessa_central?.versao_principal || ''}"

BIG IDEA:
${data.big_idea?.nome_mecanismo} - ${data.big_idea?.tagline || ''}

DIFERENCIA√á√ÉO:
${data.diferenciacao?.unico_diferenciador || ''}
`;

  // ===== CONTEXTO JSON PARA APIS =====
  const jsonContext = {
    expert_id: data.expert_id,
    expert_name: data.expert_name,

    voice: {
      temperature: data.clone_config?.temperature || 0.3,
      formality: data.clone_config?.formality_level || 7,
      provocacao: 8,
      emojis: data.comunicacao_avancada?.uso_emojis?.frequencia || 'raramente'
    },

    archetype: {
      primary: data.arquetipo_principal?.name || "The Sage",
      secondary: data.arquetipo_secundario?.name || "The Ruler"
    },

    vocabulary: {
      always: data.vocabulario?.sempre_usar || [],
      never: data.vocabulario?.nunca_usar || []
    },

    signature_phrases: data.bordoes?.principais?.map(b => b.frase) || [],

    positioning: {
      promise: data.promessa_central?.versao_principal || '',
      mechanism: data.big_idea?.nome_mecanismo || '',
      differentiator: data.diferenciacao?.unico_diferenciador || ''
    },

    avatares: data.avatares || []
  };

  // ===== MONTAR RESPOSTA =====
  formattedData.push({
    json: {
      expert_id: data.expert_id,
      expert_name: data.expert_name,

      // Dados completos originais
      full_data: data,

      // Contextos formatados
      contexts: {
        system_prompt: systemContext,
        user_message: userContext,
        json_api: jsonContext
      },

      // Metadata
      formatted_at: new Date().toISOString(),
      validation_score: data.validation?.score_total || 0
    }
  });
