const agentOutput = $input.item.json.output;

// Se j√° √© objeto, retorna
if (typeof agentOutput === 'object' && agentOutput !== null) {
  return { json: agentOutput };
}

// Converte para string
let outputStr = String(agentOutput);

try {
  // 1. Remove markdown code blocks
  outputStr = outputStr
    .replace(/```json\s*/g, '')
    .replace(/```\s*/g, '')
    .trim();

  // 2. ‚ö†Ô∏è CR√çTICO: Verifica se o JSON est√° completo
  // Conta chaves de abertura vs fechamento
  const openBraces = (outputStr.match(/\{/g) || []).length;
  const closeBraces = (outputStr.match(/\}/g) || []).length;

  if (openBraces !== closeBraces) {
    console.warn('‚ö†Ô∏è JSON INCOMPLETO detectado!');
    console.warn(`Chaves abertas: ${openBraces}, Chaves fechadas: ${closeBraces}`);

    // Tenta fechar o JSON automaticamente
    const missing = openBraces - closeBraces;
    if (missing > 0) {
      outputStr += '\n}\n'.repeat(missing);
      console.log('‚úÖ JSON fechado automaticamente');
    }
  }

  // 3. Extrai JSON principal
  const jsonMatch = outputStr.match(/\{[\s\S]*\}/);
  if (!jsonMatch) {
    throw new Error('Nenhum objeto JSON encontrado');
  }

  let jsonStr = jsonMatch[0];

  // 4. Parse
  const parsed = JSON.parse(jsonStr);

  // 5. Limpa o prompt
  if (parsed.prompt) {
    parsed.prompt = parsed.prompt
      .replace(/\\n/g, '\n')
      .replace(/\\"/g, '"')
      .replace(/\\t/g, '\t');
  }

  // 6. ‚ö†Ô∏è VERIFICA SE O PROMPT EST√Å COMPLETO
  if (parsed.prompt && !parsed.prompt.includes('EXEMPLO DE INTERA√á√ÉO COMPLETA')) {
    console.warn('‚ö†Ô∏è PROMPT PARECE INCOMPLETO - falta se√ß√£o de exemplo');
  }

  if (parsed.prompt && parsed.prompt.length < 3000) {
    console.warn(`‚ö†Ô∏è PROMPT MUITO CURTO: ${parsed.prompt.length} caracteres`);
  }

  return { json: parsed };

} catch (error) {
  // Log detalhado para debug
  console.error('‚ùå Erro no parse:', error.message);
  console.error('Output (primeiros 1000 chars):', outputStr.substring(0, 1000));
  console.error('Output (√∫ltimos 500 chars):', outputStr.substring(outputStr.length - 500));

  throw new Error(
    `Parse falhou: ${error.message}\n\n` +
    `Tamanho do output: ${outputStr.length} chars\n` +
    `In√≠cio: ${outputStr.substring(0, 200)}...`
  );
}

