const items = $input.all();
const fieldsList = [];

function safeString(value) {
  if (!value) return '';

  try {
    const str = String(value);
    // Remove caracteres problem√°ticos
    return str
      .replace(/[\u0000-\u001F\u007F-\u009F]/g, '') // Remove caracteres de controle
      .substring(0, 100);
  } catch (e) {
    return '[ERRO AO CONVERTER]';
  }
}

for (const item of items) {
  const record = item.json;

  const campos = [];

  // Listar TODOS os campos do record
  for (const fieldName in record) {
    try {
      const fieldValue = record[fieldName];
      const tipo = typeof fieldValue;
      const temConteudo = !!fieldValue && fieldValue !== '';

      let tamanho = null;
      let preview = '';

      if (fieldValue && tipo === 'string') {
        tamanho = fieldValue.length;
        preview = safeString(fieldValue);
      } else if (fieldValue) {
        preview = safeString(fieldValue);
      }

      campos.push({
        campo: fieldName,
        tipo: tipo,
        tem_conteudo: temConteudo,
        tamanho: tamanho,
        inicio: preview
      });
    } catch (e) {
      campos.push({
        campo: fieldName,
        tipo: 'ERRO',
        tem_conteudo: false,
        tamanho: null,
        inicio: 'Erro ao processar'
      });
    }
  }

  fieldsList.push({ 
    json: {
      total: campos.length,
      campos: campos
    }
  });
}

return fieldsList;

