function isString(v){return typeof v==='string'&&v.trim().length>0}
function sanitizeJsonText(t){return t.replace(/^\s*=\s*/,'').replace(/[\u201C\u201D]/g,'"').replace(/\r/g,'').replace(/,\s*([}\]])/g,'$1')}
function tryParseArray(text){
  const attempts=[]
  let t=sanitizeJsonText(text)
  try{const a=JSON.parse(t);if(Array.isArray(a)){attempts.push('json_parse');return {arr:a,attempts}}}catch(e){attempts.push('json_parse_fail')}
  try{const a=Function('return ('+t+')')();if(Array.isArray(a)){attempts.push('eval_parse');return {arr:a,attempts}}}catch(e){attempts.push('eval_parse_fail')}
  const i=t.indexOf('['), j=t.lastIndexOf(']')
  if(i>=0 && j>i){
    const slice=t.slice(i, j+1)
    try{const a=JSON.parse(slice);if(Array.isArray(a)){attempts.push('slice_json_parse');return {arr:a,attempts}}}catch(e){attempts.push('slice_json_parse_fail')}
    try{const a=Function('return ('+slice+')')();if(Array.isArray(a)){attempts.push('slice_eval_parse');return {arr:a,attempts}}}catch(e){attempts.push('slice_eval_parse_fail')}
  }
  return {arr:null,attempts}
}
function getTrilha(rec){
  const debug={has_trilha:Array.isArray(rec.trilha_completa),has_preparar:isString(rec.preparar)||typeof rec.preparar==='object',preparar_type:typeof rec.preparar}
  if(Array.isArray(rec.trilha_completa))return {arr:rec.trilha_completa,debug}
  if(!debug.has_preparar)return {arr:null,debug}
  let raw=null, hasTrilhaJson=false, parsedPrepara=false
  if(isString(rec.preparar)){
    let obj=null;try{obj=JSON.parse(rec.preparar);parsedPrepara=true}catch(e){parsedPrepara=false}
    if(obj && isString(obj.trilha_json)){hasTrilhaJson=true;raw=obj.trilha_json}
    else raw=rec.preparar
  }else if(rec.preparar && typeof rec.preparar==='object'){
    if(isString(rec.preparar.trilha_json)){hasTrilhaJson=true;raw=rec.preparar.trilha_json}
  }
  debug.parsed_preparar=parsedPrepara
  debug.has_trilha_json=hasTrilhaJson
  if(!isString(raw))return {arr:null,debug}
  const parsed=tryParseArray(raw)
  debug.parse_attempts=parsed.attempts
  return {arr:parsed.arr,debug}
}
function semanaFromDia(d){
  const n=Number(d)
  if(Number.isFinite(n)){
    if(n>=1&&n<=7)return 1
    if(n>=8&&n<=14)return 2
    if(n>=15&&n<=21)return 3
    return 4
  }
  return 1
}
function groupBySemana(trilha){
  const m=new Map()
  for(let i=0;i<trilha.length;i++){
    const item=trilha[i]||{}
    const diaVal=Number.isFinite(Number(item.dia))?Number(item.dia):(i+1)
    const w=semanaFromDia(diaVal)
    if(!m.has(w))m.set(w,[])
    m.get(w).push(item)
  }
  return Array.from(m.entries()).map(([w,arr])=>({semana:w,dias:arr}))
}
function ensureExpertData(rec){
  const ed=rec.expert_data||{}
  const fallbackSempre=['disciplina','autenticidade','pot√™ncia','coer√™ncia','seguran√ßa','precis√£o','liberta','conquista','supera','transforma','cardiovascular','hormonal','cient√≠fico','validado','reconecta','desperta','empoderamento']
  const fallbackNunca=['milagroso','m√°gico','instant√¢neo','f√°cil','garantido','revolucion√°rio (sem base)','modismo','superficial','gen√©rico']
  const vs=(isString(ed.vocabulario_sempre)&&ed.vocabulario_sempre.trim().length>0)?ed.vocabulario_sempre:(isString(rec.vocabulario_sempre)&&rec.vocabulario_sempre.trim().length>0?rec.vocabulario_sempre:fallbackSempre.join(', '))
  const vn=(isString(ed.vocabulario_nunca)&&ed.vocabulario_nunca.trim().length>0)?ed.vocabulario_nunca:(isString(rec.vocabulario_nunca)&&rec.vocabulario_nunca.trim().length>0?rec.vocabulario_nunca:fallbackNunca.join(', '))
  return {
    expert_name: ed.expert_name||rec.expert_name||'Expert',
    vocabulario_sempre: vs,
    vocabulario_nunca: vn,
    bordoes_formatados: ed.bordoes_formatados||rec.bordoes_formatados||'-'
  }
}
function process(rec){
  const {arr,debug}=getTrilha(rec)
  const ed=ensureExpertData(rec)
  if(!Array.isArray(arr) || arr.length===0){
    return [{json:{skip:true,motivo:'Sem trilha_completa',debug,expert_data:ed}}]
  }
  const weeks=groupBySemana(arr)
  return weeks.map(w=>({json:{semana:w.semana,dias:w.dias,expert_data:ed}}))
}
function inputToArray(it){
  if(!it||it.length===0)return[]
  const j=it[0].json
  if(Array.isArray(j))return j
  if(j&&typeof j==='object')return[j]
  return[]
}
const records=inputToArray(items)
let out=[]
for(const r of records){out=out.concat(process(r))}
return out

