  const ofertasFront = record['Ofertas Front End - IA'] || '';
  const dnaExtraido = record['DNA Extraido'] || '';

  // ===== 1. CLONE CONFIG =====
  const cloneConfigData = {
    // Temperature n√£o existe no Clone Config, usar default
    temperature: 0.7,

    // Formalidade: formato "**Formalidade:** 7/10"
    formality_level: extractRating(cloneConfig, 'Formalidade'),

    // Provoca√ß√£o: formato "**Provoca√ß√£o:** 8/10"
    provocacao_level: extractRating(cloneConfig, 'Provoca√ß√£o'),

    // Emojis: formato "### **Uso de Emojis (1 por par√°grafo):**"
    emojis_frequency: extractEmojisFrequency(cloneConfig),

    // Prohibited: se√ß√£o com bullets
    prohibited_behaviors: extractBullets(cloneConfig, ['NUNCA', 'Proibido', 'N√£o use']),

    // Mandatory: se√ß√£o com bullets
    mandatory_behaviors: extractBullets(cloneConfig, ['SEMPRE', 'Obrigat√≥rio'])
  };

  // ===== 2. ARQU√âTIPOS =====
  let arquetipoPrincipal = null;
  let arquetipoSecundario = null;

  // Tentar DNA primeiro
  const dnaJson = extractJSON(dnaExtraido);
  if (dnaJson && dnaJson.archetype_profile) {
    arquetipoPrincipal = dnaJson.archetype_profile.primary;
    arquetipoSecundario = dnaJson.archetype_profile.secondary;
  }

  // Fallback: Identity Map
  if (!arquetipoPrincipal && identityMap) {
    // Formato: "**Arqu√©tipo Principal:** The Hero (60%)"
    const primaryMatch = identityMap.match(/\*\*Arqu√©tipo Principal:\*\*\s+(.+?)\s+\((\d+)%\)/i);
    if (primaryMatch) {
      arquetipoPrincipal = {
        name: primaryMatch[1].trim(),
        percentage: parseInt(primaryMatch[2]),
        evidence: null
      };
    }

    const secondaryMatch = identityMap.match(/\*\*Arqu√©tipo Secund√°rio:\*\*\s+(.+?)\s+\((\d+)%\)/i);
    if (secondaryMatch) {
      arquetipoSecundario = {
        name: secondaryMatch[1].trim(),
        percentage: parseInt(secondaryMatch[2]),
        evidence: null
      };
    }
  }

  // ===== 3. VALORES =====
  let valores = {
    core_value: null,
    supporting_values: [],
    anti_values: []
  };

  const valuesJson = dnaJson ? dnaJson.value_system : null;
  if (valuesJson) {
    valores = valuesJson;
  } else if (identityMap) {
    // Extrair do formato markdown numerado
    const valoresMatch = identityMap.match(/### Valores Fundamentais[\s\S]*?(?=###|$)/i);
    if (valoresMatch) {
      const section = valoresMatch[0];

      // Valores numerados: "1. **Nome**: manifesta√ß√£o"
      const allValues = [...section.matchAll(/(\d+)\.\s*\*\*(.+?)\*\*[:\s]*(.+?)(?=\n\d+\.|###|$)/gs)];

      if (allValues.length > 0) {
        // Primeiro √© core
        valores.core_value = {
          nome: allValues[0][2].trim(),
          manifestacao: allValues[0][3].trim()
        };

        // Resto s√£o supporting
        for (let i = 1; i < allValues.length; i++) {
          valores.supporting_values.push({
            nome: allValues[i][2].trim(),
            manifestacao: allValues[i][3].trim()
          });
        }
      }

      // Anti-valores
      const antiMatch = section.match(/### Anti-Valores[\s\S]*?(?=###|$)/i);
      if (antiMatch) {
        valores.anti_values = extractBullets(antiMatch[0], null);
      }
    }
  }

  // ===== 4. BORD√ïES =====
  const bordoes = {
    principais: extractQuotedPhrases(identityMap),
    estruturas_linguisticas: {
      abertura: extractQuotedPhrases(findSectionText(identityMap, 'Frases de Abertura')),
      transicao: extractQuotedPhrases(findSectionText(identityMap, 'Frases de Transi√ß√£o')),
      fechamento: extractQuotedPhrases(findSectionText(identityMap, 'Frases de Fechamento'))
    }
  };

  // ===== 5. VOCABUL√ÅRIO =====
  const vocabulario = {
    sempre_usar: extractWordList(identityMap, 'sempre.*?usar'),
    nunca_usar: extractWordList(identityMap, 'nunca.*?usar')
  };

  // ===== 6. AVATARES =====
  const avataresData = extractAvataresFull(avatares);

  // ===== 7. PROMESSA CENTRAL =====
  const promessa = {
    versao_principal: extractMainPromise(promessaCentral),
    x_quem: extractChosenOption(promessaCentral, 'X', 'Cliente'),
    y_transformacao: extractChosenOption(promessaCentral, 'Y', 'Transforma√ß√£o'),
    versoes_alternativas: {
      formal: extractVersion(promessaCentral, 'Vers√£o 1', 'Formal'),
      conversacional: extractVersion(promessaCentral, 'Vers√£o 2', 'Conversacional'),
      emocional: extractVersion(promessaCentral, 'Vers√£o 3', 'Emocional'),
      aspiracional: extractVersion(promessaCentral, 'Vers√£o 4', 'Aspiracional'),
      provocativa: extractVersion(promessaCentral, 'Vers√£o 5', 'Provocativa')
    }
  };

  // ===== 8. BIG IDEA =====
  const bigIdeaData = {
    nome_mecanismo: extractWithMarker(bigIdea, 'Nome do Mecanismo'),
    tagline: extractWithMarker(bigIdea, 'Tagline'),
    explicacao_simples: extractExplanation(bigIdea, 'Explica√ß√£o Simples'),
    explicacao_tecnica: extractExplanation(bigIdea, 'Explica√ß√£o T√©cnica'),
    pilares: extractPilares(bigIdea)
  };

  // ===== 9. PMV =====
  const proposito = {
    declaracao: extractWithMarker(identityMap, 'Declara√ß√£o de Prop√≥sito'),
    expandido: extractWithMarker(identityMap, 'Prop√≥sito Expandido')
  };

  const missao = {
    declaracao: extractWithMarker(identityMap, 'Declara√ß√£o de Miss√£o'),
    como_realiza: extractBullets(findSectionText(identityMap, 'Como realiza'), null)
  };

  const visao = {
    futuro: extractWithMarker(identityMap, 'Vis√£o de Futuro'),
    impacto_longo_prazo: extractWithMarker(identityMap, 'Impacto de Longo Prazo')
  };

  // ===== 10. DIFERENCIA√á√ÉO =====
  const diferenciacao = {
    unico_diferenciador: extractWithMarker(identityMap, '√önico Diferenciador'),
    elementos: extractBullets(findSectionText(identityMap, 'Elementos de Diferencia√ß√£o'), null).map(e => ({descricao: e})),
    vs_concorrencia: extractWithMarker(identityMap, 'Como √© diferente')
  };

  // ===== 11. OFERTAS =====
  const ofertas = {
    high_ticket: extractOfertas(ofertasHigh, 'OFERTA', 'high'),
    back_end: extractOfertas(ofertasBack, 'OFERTA', 'back'),
    front_end: extractOfertas(ofertasFront, 'ISCA', 'front')
  };

  // ===== 12. COMUNICA√á√ÉO =====
  const comunicacao = {
    primeira_vs_segunda_pessoa: extractWithMarker(identityMap, 'Primeira vs Segunda Pessoa'),
    storytelling: extractWithMarker(identityMap, 'Storytelling'),
    provocacao_humor: {
      nivel: extractWithMarker(identityMap, 'Provoca√ß√£o e Humor'),
      estilo: null
    },
    uso_emojis: {
      frequencia: extractEmojisFrequency(cloneConfig),
      razao: null
    }
  };

  // ===== 13. ESTRAT√âGIA TR√ÅFEGO =====
  const estrategia_trafego = {
    budget_sugerido: extractNumber(bigIdea, /budget.*?R\$\s*([\d.,]+)/i),
    distribuicao: {},
    metas: {
      cpl: extractWithUnit(bigIdea, /cpl.*?(R\$\s*[\d.,\-]+)/i),
      cpa: extractWithUnit(bigIdea, /cpa.*?(R\$\s*[\d.,\-]+)/i),
      roas: extractWithUnit(bigIdea, /roas.*?([\d\-x]+)/i)
    }
  };

  // ===== MONTAR RESULTADO =====
  enrichedData.push({
    json: {
      expert_id: record.id || `expert_${Date.now()}`,
      expert_name: expertName,
      nicho: nicho,
      record_id: record.id,

      clone_config: cloneConfigData,
      arquetipo_principal: arquetipoPrincipal,
      arquetipo_secundario: arquetipoSecundario,
      valores_core: valores,
      bordoes: bordoes,
      vocabulario: vocabulario,
      avatares: avataresData,
      promessa_central: promessa,
      big_idea: bigIdeaData,
      proposito: proposito,
      missao: missao,
      visao: visao,
      diferenciacao: diferenciacao,
      ofertas: ofertas,
      comunicacao_avancada: comunicacao,
      estrategia_trafego: estrategia_trafego,

      extracted_at: new Date().toISOString(),
      version: "4.0_real_format_based",
      source_fields: {
        clone_config: !!cloneConfig,
        identity_map: !!identityMap,
        promessa: !!promessaCentral,
        big_idea: !!bigIdea,
        avatares: !!avatares,
        ofertas_high: !!ofertasHigh,
        ofertas_back: !!ofertasBack,
        ofertas_front: !!ofertasFront,
        dna_extraido: !!dnaExtraido
      }
    }
  });
}

// ===== FUN√á√ïES AUXILIARES =====

function extractRating(text, label) {
  if (!text) return null;
  const pattern = new RegExp(`\\*\\*${label}:\\*\\*\\s*(\\d+)`, 'i');
  const match = text.match(pattern);
  return match ? parseInt(match[1]) : null;
}

function extractEmojisFrequency(text) {
  if (!text) return null;
  // Formato: "### **Uso de Emojis (1 por par√°grafo):**"
  const match = text.match(/Uso de Emojis\s*\((.+?)\)/i);
  return match ? match[1].trim() : null;
}

function extractBullets(text, sectionTitles) {
  if (!text) return [];

  let section = text;

  // Se tem t√≠tulos de se√ß√£o, buscar a se√ß√£o
  if (sectionTitles && sectionTitles.length > 0) {
    for (const title of sectionTitles) {
      const pattern = new RegExp(`${title}[:\\s]*([\s\S]*?)(?=###|##|\\n\\n\\*\\*|$)`, 'i');
      const match = text.match(pattern);
      if (match) {
        section = match[1];
        break;
      }
    }
  }

  // Extrair bullets
  const bullets = [...section.matchAll(/[-‚Ä¢‚úì‚úÖ‚ùå*]\s*(.+?)(?=\n|$)/g)];
  return bullets.map(m => m[1].trim()).filter(b => b.length > 0);
}

function extractJSON(text) {
  if (!text) return null;

  try {
    // Tentar com ```json
    const jsonMatch = text.match(/```json\s*([\s\S]*?)\s*```/);
    if (jsonMatch) {
      return JSON.parse(jsonMatch[1]);
    }

    // Tentar JSON puro
    if (text.trim().startsWith('{')) {
      return JSON.parse(text);
    }
  } catch (e) {
    return null;
  }

  return null;
}

function extractQuotedPhrases(text) {
  if (!text) return [];

  const quotes = [...text.matchAll(/"([^"]{10,})"/g)];
  return quotes.map(m => ({
    frase: m[1],
    quando_usar: "Contexto a ser definido"
  }));
}

function findSectionText(text, sectionTitle) {
  if (!text) return '';

  const pattern = new RegExp(`###\\s*${sectionTitle}[\\s\\S]*?(?=###|$)`, 'i');
  const match = text.match(pattern);
  return match ? match[0] : '';
}

function extractWordList(text, pattern) {
  if (!text) return [];

  const regex = new RegExp(pattern + '[:\\s]*(.+?)(?=\\n\\*\\*|\\n\\n|$)', 'is');
  const match = text.match(regex);

  if (!match) return [];

  const content = match[1];

  // Tentar extrair de lista entre colchetes [palavra1, palavra2]
  const bracketMatch = content.match(/\[(.+?)\]/);
  if (bracketMatch) {
    return bracketMatch[1].split(',').map(w => w.trim().replace(/["\s]/g, '')).filter(Boolean);
  }

  // Tentar extrair palavras separadas por v√≠rgula
  return content.split(',').map(w => w.trim()).filter(Boolean);
}

function extractAvataresFull(text) {
  if (!text) return [];

  const avatares = [];

  // Buscar por "## AVATAR X:"
  const avatarMatches = [...text.matchAll(/##\s*AVATAR\s*(\d+):\s*(.+?)\n([\s\S]*?)(?=##\s*AVATAR|$)/gi)];

  for (const match of avatarMatches) {
    const num = match[1];
    const nome = match[2].trim();
    const content = match[3];

    const avatar = {
      id: `avatar_${num}`,
      nome: nome,
      idade: extractDemografia(content, 'Idade'),
      perfil: extractWithMarker(content, 'Ocupa√ß√£o'),
      renda_mensal: extractDemografia(content, 'Renda'),
      dor_externa: extractSubSection(content, 'Dor Externa'),
      dor_real: extractSubSection(content, 'Dor Real'),
      desejo_central: extractSubSection(content, 'Desejo Central'),
      medo_oculto: extractSubSection(content, 'Medo Oculto'),
      fit_score: null
    };

    avatares.push(avatar);
  }

  return avatares;
}

function extractDemografia(text, field) {
  const pattern = new RegExp(`\\*\\*${field}:\\*\\*\\s*(.+?)(?=\\n|$)`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractSubSection(text, title) {
  const pattern = new RegExp(`####\\s*${title}[\\s\\S]{0,20}(.{50,300})`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractMainPromise(text) {
  if (!text) return null;

  // Formato: "### Vers√£o Principal\n> **texto**"
  const match = text.match(/###\s*Vers√£o Principal[\s\S]*?>\s*\*\*(.+?)\*\*/i);
  return match ? match[1].trim() : null;
}

function extractChosenOption(text, section, field) {
  if (!text) return null;

  // Formato: "**ESCOLHIDA:** Op√ß√£o X\n**Cliente:**" ou "**Transforma√ß√£o:**"
  const pattern = new RegExp(`DEFINI√á√ÉO DO "${section}"[\\s\\S]*?ESCOLHIDA[\\s\\S]*?\\*\\*${field}:\\*\\*\\s*(.+?)(?=\\n\\*\\*|\\n\\n|$)`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractVersion(text, versionLabel, typeLabel) {
  if (!text) return null;

  const pattern = new RegExp(`###\\s*${versionLabel}:\\s*${typeLabel}[\\s\\S]*?>\\s*(.+?)(?=\\n\\n|\\*\\*|###)`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractWithMarker(text, marker) {
  if (!text) return null;

  const pattern = new RegExp(`\\*\\*${marker}:\\*\\*\\s*(.+?)(?=\\n\\n|\\*\\*|###|$)`, 'is');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractExplanation(text, type) {
  if (!text) return null;

  const pattern = new RegExp(`####\\s*${type}[\\s\\S]{0,50}(.{100,500})`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractPilares(text) {
  if (!text) return [];

  // Buscar por "**Pilar X:"
  const pilares = [...text.matchAll(/\*\*Pilar\s*\d+:\s*(.+?)\*\*/gi)];
  return pilares.map(m => m[1].trim());
}

function extractOfertas(text, keyword, type) {
  if (!text) return [];

  const ofertas = [];
  const pattern = new RegExp(`##\\s*${keyword}\\s*(\\d+)[\\s\\S]*?(?=##\\s*${keyword}|$)`, 'gi');
  const matches = [...text.matchAll(pattern)];

  for (const match of matches) {
    const num = match[1];
    const content = match[0];

    const nome = extractWithMarker(content, 'Nome') || content.match(/###\s*(.+?)(?=\n|$)/)?.[1]?.trim();
    const precoMatch = content.match(/pre√ßo.*?R\$\s*([\d.,]+)/i);

    ofertas.push({
      id: `${type}_${num}`,
      nome: nome,
      preco: precoMatch ? parseFloat(precoMatch[1].replace(/\./g, '').replace(',', '.')) : null,
      publico: extractWithMarker(content, 'P√∫blico')
    });
  }

  return ofertas;
}

function extractNumber(text, pattern) {
  if (!text) return null;
  const match = text.match(pattern);
  if (!match) return null;
  return parseFloat(match[1].replace(/\./g, '').replace(',', '.'));
}

function extractWithUnit(text, pattern) {
  if (!text) return null;
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

return enrichedData;

