function get(obj, path, def) {
  const keys = path.split('.');
  let result = obj;
  for (const key of keys) {
    if (result && typeof result === 'object' && key in result) {
      result = result[key];
    } else {
      return def;
    }
  }
  return result !== undefined ? result : def;
}

