  const visao = {
    futuro: extractWithMarker(identityMap, 'Vis√£o de Futuro'),
    impacto_longo_prazo: extractWithMarker(identityMap, 'Impacto de Longo Prazo')
  };

  // ===== 10. DIFERENCIA√á√ÉO =====
  const diferenciacao = {
    unico_diferenciador: extractWithMarker(identityMap, '√önico Diferenciador'),
    elementos: extractBullets(findSectionText(identityMap, 'Elementos de Diferencia√ß√£o'), null).map(e => ({descricao: e})),
    vs_concorrencia: extractWithMarker(identityMap, 'Como √© diferente')
  };

  // ===== 11. OFERTAS =====
  const ofertas = {
    high_ticket: extractOfertas(ofertasHigh, 'OFERTA', 'high'),
    back_end: extractOfertas(ofertasBack, 'OFERTA', 'back'),
    front_end: extractOfertas(ofertasFront, 'ISCA', 'front')
  };

  // ===== 12. COMUNICA√á√ÉO =====
  const comunicacao = {
    primeira_vs_segunda_pessoa: extractWithMarker(identityMap, 'Primeira vs Segunda Pessoa'),
    storytelling: extractWithMarker(identityMap, 'Storytelling'),
    provocacao_humor: {
      nivel: extractWithMarker(identityMap, 'Provoca√ß√£o e Humor'),
      estilo: null
    },
    uso_emojis: {
      frequencia: extractEmojisFrequency(cloneConfig),
      razao: null
    }
  };

  // ===== 13. ESTRAT√âGIA TR√ÅFEGO =====
  const estrategia_trafego = {
    budget_sugerido: extractNumber(bigIdea, /budget.*?R\$\s*([\d.,]+)/i),
    distribuicao: {},
    metas: {
      cpl: extractWithUnit(bigIdea, /cpl.*?(R\$\s*[\d.,\-]+)/i),
      cpa: extractWithUnit(bigIdea, /cpa.*?(R\$\s*[\d.,\-]+)/i),
      roas: extractWithUnit(bigIdea, /roas.*?([\d\-x]+)/i)
    }
  };

  // ===== MONTAR RESULTADO =====
  enrichedData.push({
    json: {
      expert_id: record.id || `expert_${Date.now()}`,
      expert_name: expertName,
      nicho: nicho,
      record_id: record.id,

      clone_config: cloneConfigData,
      arquetipo_principal: arquetipoPrincipal,
      arquetipo_secundario: arquetipoSecundario,
      valores_core: valores,
      bordoes: bordoes,
      vocabulario: vocabulario,
      avatares: avataresData,
      promessa_central: promessa,
      big_idea: bigIdeaData,
      proposito: proposito,
      missao: missao,
      visao: visao,
      diferenciacao: diferenciacao,
      ofertas: ofertas,
      comunicacao_avancada: comunicacao,
      estrategia_trafego: estrategia_trafego,

      extracted_at: new Date().toISOString(),
      version: "4.0_real_format_based",
      source_fields: {
        clone_config: !!cloneConfig,
        identity_map: !!identityMap,
        promessa: !!promessaCentral,
        big_idea: !!bigIdea,
        avatares: !!avatares,
        ofertas_high: !!ofertasHigh,
        ofertas_back: !!ofertasBack,
        ofertas_front: !!ofertasFront,
        dna_extraido: !!dnaExtraido
      }
    }
  });
}

// ===== FUN√á√ïES AUXILIARES =====

function extractRating(text, label) {
  if (!text) return null;
  const pattern = new RegExp(`\\*\\*${label}:\\*\\*\\s*(\\d+)`, 'i');
  const match = text.match(pattern);
  return match ? parseInt(match[1]) : null;
}

function extractEmojisFrequency(text) {
  if (!text) return null;
  // Formato: "### **Uso de Emojis (1 por par√°grafo):**"
  const match = text.match(/Uso de Emojis\s*\((.+?)\)/i);
  return match ? match[1].trim() : null;
}

function extractBullets(text, sectionTitles) {
  if (!text) return [];

  let section = text;

  // Se tem t√≠tulos de se√ß√£o, buscar a se√ß√£o
  if (sectionTitles && sectionTitles.length > 0) {
    for (const title of sectionTitles) {
      const pattern = new RegExp(`${title}[:\\s]*([\s\S]*?)(?=###|##|\\n\\n\\*\\*|$)`, 'i');
      const match = text.match(pattern);
      if (match) {
        section = match[1];
        break;
      }
    }
  }

  // Extrair bullets
  const bullets = [...section.matchAll(/[-‚Ä¢‚úì‚úÖ‚ùå*]\s*(.+?)(?=\n|$)/g)];
  return bullets.map(m => m[1].trim()).filter(b => b.length > 0);
}

function extractJSON(text) {
  if (!text) return null;

  try {
    // Tentar com ```json
    const jsonMatch = text.match(/```json\s*([\s\S]*?)\s*```/);
    if (jsonMatch) {
      return JSON.parse(jsonMatch[1]);
    }

    // Tentar JSON puro
    if (text.trim().startsWith('{')) {
      return JSON.parse(text);
    }
  } catch (e) {
    return null;
  }

  return null;
}

function extractQuotedPhrases(text) {
  if (!text) return [];

  const quotes = [...text.matchAll(/"([^"]{10,})"/g)];
  return quotes.map(m => ({
    frase: m[1],
    quando_usar: "Contexto a ser definido"
  }));
}

function findSectionText(text, sectionTitle) {
  if (!text) return '';

  const pattern = new RegExp(`###\\s*${sectionTitle}[\\s\\S]*?(?=###|$)`, 'i');
  const match = text.match(pattern);
  return match ? match[0] : '';
}

function extractWordList(text, pattern) {
  if (!text) return [];

  const regex = new RegExp(pattern + '[:\\s]*(.+?)(?=\\n\\*\\*|\\n\\n|$)', 'is');
  const match = text.match(regex);

  if (!match) return [];

  const content = match[1];

  // Tentar extrair de lista entre colchetes [palavra1, palavra2]
  const bracketMatch = content.match(/\[(.+?)\]/);
  if (bracketMatch) {
    return bracketMatch[1].split(',').map(w => w.trim().replace(/["\s]/g, '')).filter(Boolean);
  }

  // Tentar extrair palavras separadas por v√≠rgula
  return content.split(',').map(w => w.trim()).filter(Boolean);
}

function extractAvataresFull(text) {
  if (!text) return [];

  const avatares = [];

  // Buscar por "## AVATAR X:"
  const avatarMatches = [...text.matchAll(/##\s*AVATAR\s*(\d+):\s*(.+?)\n([\s\S]*?)(?=##\s*AVATAR|$)/gi)];

  for (const match of avatarMatches) {
    const num = match[1];
    const nome = match[2].trim();
    const content = match[3];

    const avatar = {
      id: `avatar_${num}`,
      nome: nome,
      idade: extractDemografia(content, 'Idade'),
      perfil: extractWithMarker(content, 'Ocupa√ß√£o'),
      renda_mensal: extractDemografia(content, 'Renda'),
      dor_externa: extractSubSection(content, 'Dor Externa'),
      dor_real: extractSubSection(content, 'Dor Real'),
      desejo_central: extractSubSection(content, 'Desejo Central'),
      medo_oculto: extractSubSection(content, 'Medo Oculto'),
      fit_score: null
    };

    avatares.push(avatar);
  }

  return avatares;
}

function extractDemografia(text, field) {
  const pattern = new RegExp(`\\*\\*${field}:\\*\\*\\s*(.+?)(?=\\n|$)`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractSubSection(text, title) {
  const pattern = new RegExp(`####\\s*${title}[\\s\\S]{0,20}(.{50,300})`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractMainPromise(text) {
  if (!text) return null;

  // Formato: "### Vers√£o Principal\n> **texto**"
  const match = text.match(/###\s*Vers√£o Principal[\s\S]*?>\s*\*\*(.+?)\*\*/i);
  return match ? match[1].trim() : null;
}

function extractChosenOption(text, section, field) {
  if (!text) return null;

  // Formato: "**ESCOLHIDA:** Op√ß√£o X\n**Cliente:**" ou "**Transforma√ß√£o:**"
  const pattern = new RegExp(`DEFINI√á√ÉO DO "${section}"[\\s\\S]*?ESCOLHIDA[\\s\\S]*?\\*\\*${field}:\\*\\*\\s*(.+?)(?=\\n\\*\\*|\\n\\n|$)`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractVersion(text, versionLabel, typeLabel) {
  if (!text) return null;

  const pattern = new RegExp(`###\\s*${versionLabel}:\\s*${typeLabel}[\\s\\S]*?>\\s*(.+?)(?=\\n\\n|\\*\\*|###)`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractWithMarker(text, marker) {
  if (!text) return null;

  const pattern = new RegExp(`\\*\\*${marker}:\\*\\*\\s*(.+?)(?=\\n\\n|\\*\\*|###|$)`, 'is');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractExplanation(text, type) {
  if (!text) return null;

  const pattern = new RegExp(`####\\s*${type}[\\s\\S]{0,50}(.{100,500})`, 'i');
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

function extractPilares(text) {
  if (!text) return [];

  // Buscar por "**Pilar X:"
  const pilares = [...text.matchAll(/\*\*Pilar\s*\d+:\s*(.+?)\*\*/gi)];
  return pilares.map(m => m[1].trim());
}

function extractOfertas(text, keyword, type) {
  if (!text) return [];

  const ofertas = [];
  const pattern = new RegExp(`##\\s*${keyword}\\s*(\\d+)[\\s\\S]*?(?=##\\s*${keyword}|$)`, 'gi');
  const matches = [...text.matchAll(pattern)];

  for (const match of matches) {
    const num = match[1];
    const content = match[0];

    const nome = extractWithMarker(content, 'Nome') || content.match(/###\s*(.+?)(?=\n|$)/)?.[1]?.trim();
    const precoMatch = content.match(/pre√ßo.*?R\$\s*([\d.,]+)/i);

    ofertas.push({
      id: `${type}_${num}`,
      nome: nome,
      preco: precoMatch ? parseFloat(precoMatch[1].replace(/\./g, '').replace(',', '.')) : null,
      publico: extractWithMarker(content, 'P√∫blico')
    });
  }

  return ofertas;
}

function extractNumber(text, pattern) {
  if (!text) return null;
  const match = text.match(pattern);
  if (!match) return null;
  return parseFloat(match[1].replace(/\./g, '').replace(',', '.'));
}

function extractWithUnit(text, pattern) {
  if (!text) return null;
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
}

return enrichedData;

// ============================================
// ORGANIZADOR DE EXPERT DATA
// Transforma o output atual em estrutura organizada
// ============================================

const items = $input.all();
const organized = [];

for (const item of items) {
  const raw = item.json;

  // Organizar dados em estrutura limpa
  const expertData = {
    // === IDENTIFICA√á√ÉO ===
    expert_name: raw.expert_name || 'Expert sem nome',
    expert_id: raw.expert_id || null,

    // === CLONE CONFIG ===
    clone_config: {
      temperature: raw.temperature || 0.7,
      formality_level: raw.formality || 7,
      emotional_range: raw.emotional_range || 'Equilibrado',
      uso_emojis: raw.uso_emojis || null
    },

    // === ARQU√âTIPOS ===
    arquetipo_principal: {
      name: raw.arquetipo_principal || null,
      percentage: raw.arquetipo_principal_pct || null
    },

    arquetipo_secundario: {
      name: raw.arquetipo_secundario || null,
      percentage: raw.arquetipo_secundario_pct || null
    },

    // === VALORES ===
    valores_core: {
      core_value: {
        nome: raw.core_value_nome || null,
        manifestacao: raw.core_value_manifestacao || null
      },
      supporting_values: parseListItems(raw.supporting_values_formatados),
      anti_values: []
    },

    // === BORD√ïES ===
    bordoes: {
      principais: parseQuotedPhrases(raw.bordoes_formatados),
      frases_abertura: [],
      frases_transicao: [],
      frases_fechamento: []
    },

    // === VOCABUL√ÅRIO ===
    vocabulario: {
      sempre_usar: parseWordList(raw.vocabulario_sempre),
      nunca_usar: parseWordList(raw.vocabulario_nunca)
    },

    // === PROMESSA E POSICIONAMENTO ===
    promessa_central: raw.promessa || null,
    big_idea: {
      mecanismo: raw.mecanismo || 'Mecanismo n√£o definido',
      tagline: raw.tagline || null
    },

    // === DIFERENCIA√á√ÉO ===
    diferenciacao: raw.diferenciacao || null,

    // === PROP√ìSITO E MISS√ÉO ===
    proposito: raw.proposito || null,
    missao: raw.missao || null,

    // === METADATA ===
    extracted_at: new Date().toISOString(),
    version: "organized_v1.0"
  };

  organized.push({
    json: {
      expert_data: expertData,
      // Manter dados originais tamb√©m
      ...raw
    }
  });
}

// === FUN√á√ïES AUXILIARES ===

function parseQuotedPhrases(text) {
  if (!text) return [];

  const phrases = [];
  const lines = text.split('\n');

  for (const line of lines) {
    // Extrair frases entre aspas
    const matches = line.match(/"([^"]+)"/);
    if (matches && matches[1]) {
      // Extrair contexto "quando:"
      const whenMatch = line.match(/quando:\s*(.+?)(?:\n|$)/);
      phrases.push({
        frase: matches[1].trim(),
        quando_usar: whenMatch ? whenMatch[1].trim() : 'Contexto a ser definido'
      });
    }
  }

  return phrases;
}

function parseListItems(text) {
  if (!text) return [];

  const items = [];
  const lines = text.split('\n');

  for (const line of lines) {
    // Formato: "- Nome: Descri√ß√£o"
    const match = line.match(/^-\s*([^:]+):\s*(.+)/);
    if (match) {
      items.push({
        nome: match[1].trim(),
        manifestacao: match[2].trim()
      });
    }
  }

  return items;
}

function parseWordList(text) {
  if (!text) return [];

  // Remove marcadores de lista e separa por v√≠rgula
  const cleaned = text.replace(/^\):\*\*\n/g, '').replace(/\n/g, '');
  const words = cleaned.split(',').map(w => w.trim()).filter(w => w.length > 0);

  return words;
}

return organized;

