function isString(v){return typeof v==='string'&&v.trim().length>0}
function clean(s){return isString(s)?s.replace(/\s+/g,' ').trim():s}
function shortPhrase(s){return isString(s)&&s.length<=120&&!/\n/.test(s)}
function dedupe(arr){const set=new Set();const out=[];for(const x of arr){const k=(x||'').toLowerCase();if(!set.has(k)){set.add(k);out.push(x)}}return out}

function gatherPhrases(rec){
  const out=[]
  const full=rec.full_data&&rec.full_data.bordoes&&Array.isArray(rec.full_data.bordoes.principais)?rec.full_data.bordoes.principais:[]
  for(const b of full){if(isString(b&&b.frase))out.push(clean(b.frase))}
  const sig=rec.contexts&&Array.isArray(rec.contexts.json_api&&rec.contexts.json_api.signature_phrases)?rec.contexts.json_api.signature_phrases:[]
  for(const s of sig){if(isString(s))out.push(clean(s))}
  if(out.length===0){
    const fallback=[
      'negociar a verdade e coer√™ncia interna',
      'seguran√ßa cardiovascular',
      'base cient√≠fica real',
      'precis√£o cl√≠nica',
      'mulheres a se reconectarem com sua identidade, autoestima e pot√™ncia',
      'transforma√ß√£o √© poss√≠vel em qualquer idade',
      'sua pot√™ncia n√£o pede permiss√£o para existir'
    ]
    return fallback
  }
  return out
}

function formatBordoes(rec){
  const phrases=gatherPhrases(rec).filter(shortPhrase)
  const uniq=dedupe(phrases)
  if(uniq.length===0)return '-'
  return uniq.map(f=>'- '+f).join('\n')
}

function processRecord(rec){
  const bf=formatBordoes(rec)
  if(!rec.expert_data)rec.expert_data={}
  rec.expert_data.bordoes_formatados=bf
  return rec
}

function inputToArray(it){
  if(!it||it.length===0)return[]
  const j=it[0].json
  if(Array.isArray(j))return j
  if(j&&typeof j==='object')return[j]
  return[]
}

const records=inputToArray(items)
const result=records.map(processRecord)
return result.map(r=>({json:r}))

