  console.log('==============================================');
  console.log('PROCESSANDO CARROSSEIS');
  console.log('==============================================');
  console.log('Total de items recebidos:', items.length);

  // PEGAR EXPERT_ID
  let expertId = null;

  try {
    expertId = $('Get Expert DNA2').first().json.id;
    console.log('Expert ID encontrado:', expertId);
  } catch (error) {
    console.log('Tentando buscar Expert ID do item...');
    try {
      expertId = items[0].json.expert_id || items[0].json.id;
    } catch (error2) {
      console.log('Nao foi possivel encontrar Expert ID');
    }
  }

  // PROCESSA CADA SEMANA
  const semanas = {};

  items.forEach((item, index) => {
    const agentOutput = item.json?.output;
    const semana = item.json?.semana;

    console.log('\nProcessando Item', index + 1, '- Semana', semana);

    if (!agentOutput || !semana) {
      console.log('  AVISO: output ou semana nao encontrado');
      return;
    }

    // Remove markdown code blocks
    let cleaned = typeof agentOutput === 'string'
      ? agentOutput.replace(/```json\s*/g, '').replace(/```\s*/g, '').trim()
      : JSON.stringify(agentOutput);

    try {
      const parsed = JSON.parse(cleaned);

      console.log('  Estrutura detectada:', Object.keys(parsed));
      console.log('  E array?', Array.isArray(parsed));

      let carrosseis = [];

      // DETECTA ESTRUTURA

      // Caso 1: Array direto de carrosseis (Semanas 1 e 3)
      if (Array.isArray(parsed)) {
        console.log('  Estrutura: Array direto');
        carrosseis = parsed.map(item => item.carrossel).filter(c => c);
      }
      // Caso 2: Objeto com propriedade "carrosseis" (plural) - Semana 4
      else if (parsed.carrosseis && Array.isArray(parsed.carrosseis)) {
        console.log('  Estrutura: Objeto com carrosseis (plural) array');
        carrosseis = parsed.carrosseis.map(item => item.carrossel).filter(c => c);
      }
      // Caso 3: Objeto com propriedade "carrossel" que eh array (Semana 3 alternativa)
      else if (parsed.carrossel && Array.isArray(parsed.carrossel)) {
        console.log('  Estrutura: Objeto com carrossel array');
        carrosseis = parsed.carrossel;
      }
      // Caso 4: Objeto com propriedade "carrossel" que eh objeto (Semana 2)
      else if (parsed.carrossel && typeof parsed.carrossel === 'object') {
        console.log('  Estrutura: Objeto com carrossel objeto');
        carrosseis = [parsed.carrossel];
      }

      console.log('  Carrosseis encontrados:', carrosseis.length);

      if (carrosseis.length === 0) {
        console.log('  Sem carrosseis validos');
        semanas['semana_' + semana] = {
          carrosseis_texto: '',
          carrosseis_json: '[]',
          total_carrosseis: 0
        };
        return;
      }

      // FORMATA OS CARROSSEIS
      const carrosseisFormatados = carrosseis.map((c) => {
        const dia = c.dia_trilha || 'N/A';
        const totalSlides = c.slides ? c.slides.length : 0;

        let resultado = '================================================================\n';
        resultado += 'CARROSSEL DIA ' + dia + ' | ' + (c.tema || 'N/A') + '\n';
        resultado += '================================================================\n\n';
        resultado += 'OBJETIVO: ' + (c.objetivo || 'N/A') + '\n';
        resultado += 'TEMPLATE: ' + (c.template_usado || 'N/A') + '\n\n';
        resultado += 'TOTAL DE SLIDES: ' + totalSlides + '\n\n';

        // SLIDES
        if (c.slides && c.slides.length > 0) {
          resultado += 'SLIDES:\n\n';

          c.slides.forEach(slide => {
            resultado += '  SLIDE ' + slide.numero + ':\n';
            resultado += '  Tipo: ' + (slide.tipo || 'N/A') + '\n';

            if (slide.titulo) resultado += '  Titulo: ' + slide.titulo + '\n';
            if (slide.titulo_slide) resultado += '  Titulo: ' + slide.titulo_slide + '\n';
            if (slide.subtitulo) resultado += '  Subtitulo: ' + slide.subtitulo + '\n';
            if (slide.conteudo) resultado += '  Conteudo: ' + slide.conteudo + '\n';

            if (slide.bullets && slide.bullets.length > 0) {
              resultado += '  Bullets:\n';
              slide.bullets.forEach(b => {
                resultado += '     - ' + b + '\n';
              });
            }

            if (slide.elementos_visuais_sugeridos) {
              resultado += '  Visual: ' + slide.elementos_visuais_sugeridos + '\n';
            }
            if (slide.nota_design) {
              resultado += '  Design: ' + slide.nota_design + '\n';
            }

            resultado += '\n';
          });
        }

        // LEGENDA
        if (c.legenda) {
          resultado += 'LEGENDA:\n';
          resultado += '  Gancho: ' + (c.legenda.gancho || 'N/A') + '\n';
          resultado += '  Corpo: ' + (c.legenda.corpo || 'N/A') + '\n';
          resultado += '  CTA: ' + (c.legenda.cta || 'N/A') + '\n';

          if (c.legenda.hashtags && c.legenda.hashtags.length > 0) {
            resultado += '  Hashtags: ' + c.legenda.hashtags.join(' ') + '\n';
          }
          resultado += '\n';
        }

        // METRICAS
        if (c.metricas_esperadas) {
          resultado += 'METRICAS ESPERADAS:\n';
          resultado += '  Saves: ' + (c.metricas_esperadas.saves || 'N/A') + '\n';
          resultado += '  Shares: ' + (c.metricas_esperadas.shares || 'N/A') + '\n';
          resultado += '  Comentarios: ' + (c.metricas_esperadas.comentarios || 'N/A') + '\n';
          resultado += '  Conversoes: ' + (c.metricas_esperadas.conversoes || 'N/A') + '\n';
        }

        return resultado;
      }).join('\n\n');

      semanas['semana_' + semana] = {
        carrosseis_texto: carrosseisFormatados,
        carrosseis_json: JSON.stringify(carrosseis, null, 2),
        total_carrosseis: carrosseis.length
      };

      console.log('  Carrosseis formatados:', carrosseis.length);

    } catch (error) {
      console.error('  ERRO ao processar semana', semana, ':', error.message);
      semanas['semana_' + semana] = {
        carrosseis_texto: '',
        carrosseis_json: '[]',
        total_carrosseis: 0
      };
    }
  });

  // COMBINA TODOS OS CARROSSEIS
  const todosCarrosseis = [1, 2, 3, 4]
    .map(num => {
      const s = semanas['semana_' + num];
      if (!s || !s.carrosseis_texto) return '';

      return '\n\n================================================================\n' +
             'SEMANA ' + num + '\n' +
             '================================================================\n\n' +
             s.carrosseis_texto;
    })
    .filter(t => t.length > 0)
    .join('\n\n');

  const todosCarrosseisJSON = [1, 2, 3, 4]
    .flatMap(num => {
      const s = semanas['semana_' + num];
      if (!s || !s.carrosseis_json) return [];
      try {
        return JSON.parse(s.carrosseis_json);
      } catch {
        return [];
      }
    });

  const totalCarrosseis = Object.values(semanas).reduce((sum, s) => sum + (s.total_carrosseis || 0), 0);

  console.log('');
  console.log('==============================================');
  console.log('PROCESSAMENTO COMPLETO');
  console.log('==============================================');
  console.log('Total de carrosseis:', totalCarrosseis);
  console.log('');
  console.log('Carrosseis por semana:');
  console.log('  Semana 1:', (semanas.semana_1?.total_carrosseis || 0), 'carrosseis');
  console.log('  Semana 2:', (semanas.semana_2?.total_carrosseis || 0), 'carrosseis');
  console.log('  Semana 3:', (semanas.semana_3?.total_carrosseis || 0), 'carrosseis');
  console.log('  Semana 4:', (semanas.semana_4?.total_carrosseis || 0), 'carrosseis');
  console.log('==============================================');

  return {
    json: {
      expert_id: expertId,
      carrosseis_completo_texto: todosCarrosseis,
      carrosseis_completo_json: JSON.stringify(todosCarrosseisJSON, null, 2),
      carrosseis_semana_1: semanas.semana_1?.carrosseis_texto || '',
      carrosseis_semana_2: semanas.semana_2?.carrosseis_texto || '',
      carrosseis_semana_3: semanas.semana_3?.carrosseis_texto || '',
      carrosseis_semana_4: semanas.semana_4?.carrosseis_texto || '',
      total_carrosseis: totalCarrosseis,
      data_processamento: new Date().toISOString()
    }
  };

