// PROCESSA CADA SEMANA
const semanas = {};

items.forEach((item, index) => {
  const agentOutput = item.json?.output;
  const semana = item.json?.semana;

  console.log('\nProcessando Item', index + 1, '- Semana', semana);

  if (!agentOutput || !semana) {
    console.log('  AVISO: output ou semana nao encontrado');
    return;
  }

  // Remove markdown code blocks
  let cleaned = typeof agentOutput === 'string'
    ? agentOutput.replace(/```json\s*/g, '').replace(/```\s*/g, '').trim()
    : JSON.stringify(agentOutput);

  try {
    const parsed = JSON.parse(cleaned);

    console.log('  Estrutura detectada:', Object.keys(parsed));
    console.log('  E array?', Array.isArray(parsed));

    let stories = [];

    // DETECTA ESTRUTURA

    // Caso 1: Objeto com propriedade "stories" (plural)
    if (parsed.stories && Array.isArray(parsed.stories)) {
      console.log('  Estrutura: Objeto com stories array');
      stories = parsed.stories;
    }
    // Caso 2: Objeto com "stories_semana_X" (Semana 3 com estrutura diferente)
    else if (parsed.stories_semana_1 || parsed.stories_semana_2 || parsed.stories_semana_3 ||
parsed.stories_semana_4) {
      console.log('  Estrutura: Objeto com stories_semana_X');
      const storiesKey = Object.keys(parsed).find(key => key.startsWith('stories_semana_'));
      if (storiesKey && Array.isArray(parsed[storiesKey])) {
        stories = parsed[storiesKey];
      }
    }
    // Caso 3: Objeto com propriedade "story" (singular) que eh array
    else if (parsed.story && Array.isArray(parsed.story)) {
      console.log('  Estrutura: Objeto com story array');
      stories = parsed.story;
    }
    // Caso 4: Objeto com propriedade "story" (singular) que eh objeto
    else if (parsed.story && typeof parsed.story === 'object') {
      console.log('  Estrutura: Objeto com story objeto');
      stories = [parsed.story];
    }
    // Caso 5: Array direto
    else if (Array.isArray(parsed)) {
      console.log('  Estrutura: Array direto');
      stories = parsed;
    }

    console.log('  Stories encontrados:', stories.length);

    if (stories.length === 0) {
      console.log('  Sem stories validos');
      semanas['semana_' + semana] = {
        stories_texto: '',
        stories_json: '[]',
        total_stories: 0
      };
      return;
    }

    // FORMATA OS STORIES
    // Extrai story_sequence se existir
    const storiesProcessados = stories.map(s => s.story_sequence || s);

    const storiesFormatados = storiesProcessados.map((s) => {
      const dia = s.dia_trilha || 'N/A';
      const totalCards = s.cards ? s.cards.length : 0;

      let resultado = '================================================================\n';
      resultado += 'STORY DIA ' + dia + ' | ' + (s.tema || 'N/A') + '\n';
      resultado += '================================================================\n\n';
      resultado += 'FORMATO: ' + (s.formato || 'N/A') + '\n';
      resultado += 'OBJETIVO: ' + (s.objetivo || 'N/A') + '\n';
      resultado += 'TEMPLATE: ' + (s.template_usado || 'N/A') + '\n';
      resultado += 'DURACAO TOTAL: ' + (s.duracao_total_estimada || 'N/A') + '\n\n';
      resultado += 'TOTAL DE CARDS: ' + totalCards + '\n\n';

      // CARDS
      if (s.cards && s.cards.length > 0) {
        resultado += 'CARDS:\n\n';

        s.cards.forEach(card => {
          resultado += '  CARD ' + card.numero + ':\n';
          resultado += '  Tipo: ' + (card.tipo || 'N/A') + '\n';
          resultado += '  Duracao: ' + (card.duracao_segundos || 0) + 's\n';

          // Visual
          if (card.visual) {
            resultado += '  Visual:\n';
            resultado += '    - Tipo: ' + (card.visual.tipo || 'N/A') + '\n';
            resultado += '    - Fundo: ' + (card.visual.fundo || 'N/A') + '\n';
            if (card.visual.elementos && card.visual.elementos.length > 0) {
              resultado += '    - Elementos: ' + card.visual.elementos.join(', ') + '\n';
            }
          }

          // Conteudo
          if (card.conteudo) {
            resultado += '  Conteudo:\n';
            if (card.conteudo.titulo) resultado += '    - Titulo: ' + card.conteudo.titulo + '\n';
            if (card.conteudo.texto_principal) resultado += '    - Texto: ' + card.conteudo.texto_principal +
'\n';
            if (card.conteudo.subtexto) resultado += '    - Subtexto: ' + card.conteudo.subtexto + '\n';
            if (card.conteudo.emoji) resultado += '    - Emoji: ' + card.conteudo.emoji + '\n';

            if (card.conteudo.bullets && card.conteudo.bullets.length > 0) {
              resultado += '    - Bullets:\n';
              card.conteudo.bullets.forEach(b => {
                resultado += '       ' + b + '\n';
              });
            }
          }

          if (card.objetivo_card) {
            resultado += '  Objetivo: ' + card.objetivo_card + '\n';
          }

          resultado += '\n';
        });
      }

      // CTA FINAL
      if (s.cta_final) {
        resultado += 'CTA FINAL:\n';
        resultado += '  Tipo: ' + (s.cta_final.tipo || 'N/A') + '\n';
        resultado += '  Texto: ' + (s.cta_final.texto_cta || 'N/A') + '\n';
        if (s.cta_final.palavra_chave) {
          resultado += '  Palavra-chave: ' + s.cta_final.palavra_chave + '\n';
        }
        resultado += '  Acao esperada: ' + (s.cta_final.acao_esperada || 'N/A') + '\n\n';
      }

      // METRICAS
      if (s.metricas_esperadas) {
        resultado += 'METRICAS ESPERADAS:\n';
        resultado += '  Visualizacoes completas: ' + (s.metricas_esperadas.visualizacoes_completas || 'N/A') +
'\n';
        resultado += '  Interacoes: ' + (s.metricas_esperadas.interacoes || 'N/A') + '\n';
        resultado += '  Conversoes: ' + (s.metricas_esperadas.conversoes || 'N/A') + '\n';
      }

      return resultado;
    }).join('\n\n');

    semanas['semana_' + semana] = {
      stories_texto: storiesFormatados,
      stories_json: JSON.stringify(storiesProcessados, null, 2),
      total_stories: storiesProcessados.length
    };

    console.log('  Stories formatados:', storiesProcessados.length);

  } catch (error) {
    console.error('  ERRO ao processar semana', semana, ':', error.message);
    semanas['semana_' + semana] = {
      stories_texto: '',
      stories_json: '[]',
      total_stories: 0
    };
  }
});

// COMBINA TODOS OS STORIES
const todosStories = [1, 2, 3, 4]
  .map(num => {
    const s = semanas['semana_' + num];
    if (!s || !s.stories_texto) return '';

    return '\n\n================================================================\n' +
           'SEMANA ' + num + '\n' +
           '================================================================\n\n' +
           s.stories_texto;
  })
  .filter(t => t.length > 0)
  .join('\n\n');

const todosStoriesJSON = [1, 2, 3, 4]
  .flatMap(num => {
    const s = semanas['semana_' + num];
    if (!s || !s.stories_json) return [];
    try {
      return JSON.parse(s.stories_json);
    } catch {
      return [];
    }
  });

const totalStories = Object.values(semanas).reduce((sum, s) => sum + (s.total_stories || 0), 0);

console.log('');
console.log('==============================================');
console.log('PROCESSAMENTO COMPLETO');
console.log('==============================================');
console.log('Total de stories:', totalStories);
console.log('');
console.log('Stories por semana:');
console.log('  Semana 1:', (semanas.semana_1?.total_stories || 0), 'stories');
console.log('  Semana 2:', (semanas.semana_2?.total_stories || 0), 'stories');
console.log('  Semana 3:', (semanas.semana_3?.total_stories || 0), 'stories');
console.log('  Semana 4:', (semanas.semana_4?.total_stories || 0), 'stories');
console.log('==============================================');

return {
  json: {
    expert_id: expertId,
    stories_completo_texto: todosStories,
    stories_completo_json: JSON.stringify(todosStoriesJSON, null, 2),
    stories_semana_1: semanas.semana_1?.stories_texto || '',
    stories_semana_2: semanas.semana_2?.stories_texto || '',
    stories_semana_3: semanas.semana_3?.stories_texto || '',
    stories_semana_4: semanas.semana_4?.stories_texto || '',
    total_stories: totalStories,
    data_processamento: new Date().toISOString()
  }
};

