  if(rec.expert_data&&isString(rec.expert_data.arquetipo_principal))return rec.expert_data.arquetipo_principal
  let name=null, perc=null
  if(rec.arquetipo_principal){ name=rec.arquetipo_principal.name; perc=rec.arquetipo_principal.percentage }
