    return;
  }

  // Remove markdown code blocks
  let cleaned = typeof agentOutput === 'string'
    ? agentOutput.replace(/```json\s*/g, '').replace(/```\s*/g, '').trim()
    : JSON.stringify(agentOutput);

  try {
    const parsed = JSON.parse(cleaned);

    console.log('  Estrutura detectada:', Object.keys(parsed));

    let posts = [];

    // DETECTA ESTRUTURA
    if (parsed.posts_reflexivos && Array.isArray(parsed.posts_reflexivos)) {
      console.log('  Estrutura: posts_reflexivos (array)');
      posts = parsed.posts_reflexivos.map(p => p.post_reflexivo);
    }
    else if (parsed.post_reflexivo) {
      console.log('  Estrutura: post_reflexivo (singular)');
      posts = [parsed.post_reflexivo];
    }
    else if (Array.isArray(parsed)) {
      console.log('  Estrutura: Array direto');
      posts = parsed;
    }

    console.log('  Posts encontrados:', posts.length);

    if (posts.length === 0) {
      console.log('  Sem posts validos');
      semanas['semana_' + semana] = {
        posts_texto: '',
        posts_json: '[]',
        total_posts: 0
      };
      return;
    }

    // FORMATA OS POSTS
    const postsFormatados = posts.map((p) => {
      const copy = p.copy_completo || {};
      const dia = p.dia_trilha || p.dia || 'N/A';

      let textoCompleto = '';

      if (copy.gancho_pre_ver_mais || copy.corpo_completo) {
        textoCompleto =
          (copy.gancho_pre_ver_mais ? copy.gancho_pre_ver_mais + '\n\n' : '') +
          (copy.corpo_completo ? copy.corpo_completo + '\n\n' : '') +
          (copy.cta_final ? copy.cta_final : '');
      } else {
        textoCompleto = 'Conteudo estruturado - ver JSON para detalhes';
      }

      let resultado = '================================================================\n';
      resultado += 'DIA ' + dia + '\n';
      resultado += '================================================================\n\n';
      resultado += 'TEMA: ' + (p.tema || 'N/A') + '\n';

      if (p.estrutura_usada) resultado += 'ESTRUTURA: ' + p.estrutura_usada + '\n';
      if (p.objetivo) resultado += 'OBJETIVO: ' + p.objetivo + '\n';
      if (p.temperatura_lead) resultado += 'TEMPERATURA: ' + p.temperatura_lead + '\n\n';
      else resultado += '\n';

      resultado += 'TEXTO COMPLETO:\n' + textoCompleto + '\n\n';

      if (copy.hashtags) resultado += 'HASHTAGS: ' + copy.hashtags.join(' ') + '\n\n';

      if (p.metricas_esperadas) {
        resultado += 'METRICAS ESPERADAS:\n';
        resultado += '  Saves: ' + (p.metricas_esperadas.saves || 'N/A') + '\n';
        resultado += '  Comentarios: ' + (p.metricas_esperadas.comentarios || 'N/A') + '\n';
        resultado += '  Shares: ' + (p.metricas_esperadas.shares || 'N/A') + '\n';
        resultado += '  Conversoes: ' + (p.metricas_esperadas.conversoes || 'N/A') + '\n';
      }

      if (p.notas_estrategicas) {
        resultado += '\nNOTAS ESTRATEGICAS:\n';
        resultado += '  Melhor horario: ' + (p.notas_estrategicas.melhor_dia_horario || 'N/A') + '\n';
        resultado += '  Tipo audiencia: ' + (p.notas_estrategicas.tipo_audiencia || 'N/A') + '\n';
        resultado += '  Sensibilidade: ' + (p.notas_estrategicas.sensibilidade || 'N/A') + '\n';
      }

      return resultado;
    }).join('\n\n');

    semanas['semana_' + semana] = {
      posts_texto: postsFormatados,
      posts_json: JSON.stringify(posts, null, 2),
      total_posts: posts.length
    };

    console.log('  Posts formatados:', posts.length);

  } catch (error) {
    console.error('  ERRO ao processar semana', semana, ':', error.message);
    semanas['semana_' + semana] = {
      posts_texto: '',
      posts_json: '[]',
      total_posts: 0
    };
  }
});

