}
// Add new content if it exists
if (typeof $json.output?.new_article_content === 'string') {
  s.article_so_far += $json.output.new_article_content;
}
return {
  new_article_content: $json.output?.new_article_content || null,
  article_so_far: s.article_so_far
};

