// FUNCTION node (not Function Item)
// Input items should have a field "SetApolloURL" that contains the Apollo UI URL.
// Falls back to "apollo_url" or "url" if present.
// Output per item:
// { json: { people_search_payload: <mapped payload>, unmapped_params: <array> } }

const KEY_MAP = {
  // paging/sort
  page: "page",
  per_page: "per_page",
  sortByField: "sort_by_field",
  sortAscending: "sort_ascending",

  // email filters
  contactEmailExcludeCatchAll: "contact_email_exclude_catch_all",
  contactEmailStatusV2: "contact_email_status_v2",
  extrapolatedEmailConfidence: "extrapolated_email_confidence",

  // person filters
  personTitles: "person_titles",
  personSeniorities: "person_seniorities",
  personLocations: "person_locations",

  // org filters
  organizationNumEmployeesRanges: "organization_num_employees_ranges",
  organizationLocations: "organization_locations",
  organizationDomains: "organization_domains",
  industries: "industries",

  // keywords / tech / intent
  includedOrganizationKeywordFields: "included_organization_keyword_fields",
  excludedOrganizationKeywordFields: "excluded_organization_keyword_fields",
  currentlyUsingAnyOfTechnologyUids: "currently_using_any_of_technology_uids",
  intentStrengths: "intent_strengths",
};

function toSnake(s){
  return s.replace(/\[\]$/,'').replace(/[A-Z]/g, m => `_${m.toLowerCase()}`);
}
function coerce(v){
  if (v === "true") return true;
  if (v === "false") return false;
  if (/^-?\d+$/.test(v)) return Number(v);
  return v;
}
function decodePlus(s){
  // handle '+' as space like application/x-www-form-urlencoded
  return decodeURIComponent((s || "").replace(/\+/g, " "));
}

// Minimal query parser that supports repeated keys and [] keys
function parseQueryString(qs){
  const map = new Map();
  if (!qs) return map;
  for (const pair of qs.split("&")) {
    if (!pair) continue;
    const eq = pair.indexOf("=");
    const rawK = eq >= 0 ? pair.slice(0, eq) : pair;
    const rawV = eq >= 0 ? pair.slice(eq + 1) : "";
    const k = decodePlus(rawK);
    const v = coerce(decodePlus(rawV));
    if (!map.has(k)) map.set(k, []);
    map.get(k).push(v);
  }
  return map;
}

function extractQueryFromApolloUrl(urlStr){
  // Apollo typically: https://app.apollo.io/#/people?<filters>
  // 1) Try hash segment
  const hashPos = urlStr.indexOf("#");
  if (hashPos >= 0) {
    const hashPart = urlStr.slice(hashPos + 1); // e.g. "/people?page=1&..."
    const qPos = hashPart.indexOf("?");
    if (qPos >= 0) return hashPart.slice(qPos + 1);
  }
  // 2) Fallback: normal query (?...) on the URL
  const qPos2 = urlStr.indexOf("?");
  if (qPos2 >= 0) return urlStr.slice(qPos2 + 1);
  return "";
}

function getAll(map, key){
  const direct = map.get(key) || [];
  const bracket = map.get(`${key}[]`) || [];
  return [...direct, ...bracket];
}

function parseApolloPeopleUrl(urlStr){
  const qs = extractQueryFromApolloUrl(urlStr);
  const p = parseQueryString(qs);

  const out = {};
  const seen = new Set();

  // Map known keys
  for (const [uiKey, apiKey] of Object.entries(KEY_MAP)) {
    const vals = getAll(p, uiKey);
    if (!vals.length) continue;

    if (vals.length > 1) out[apiKey] = vals;
    else {
      const v = vals[0];
      if (/_ranges$|_fields$|_uids$|_titles$|_seniorities$|_locations$|_domains$|industries$|_status_v2$|_confidence$/.test(apiKey)) {
        out[apiKey] = [v];
      } else {
        out[apiKey] = v;
      }
    }
    seen.add(uiKey); seen.add(`${uiKey}[]`);
  }

  // Also accept page/per_page/leads if present
  if (!("page" in out) && p.get("page")) out.page = Number(p.get("page")[0]);
  if (!("per_page" in out) && p.get("per_page")) out.per_page = Number(p.get("per_page")[0]);
  if (!("per_page" in out) && p.get("leads")) out.per_page = Number(p.get("leads")[0]);

  // Collect unknowns so you can extend KEY_MAP later
  const unknown = [];
  for (const [k, vals] of p.entries()) {
    if (seen.has(k) || !k) continue;
    unknown.push({ uiKey: k, value: vals.length > 1 ? vals : vals[0], suggestion: toSnake(k) });
  }

  // Clean empties
  for (const k of Object.keys(out)) {
    if (out[k] == null || (Array.isArray(out[k]) && out[k].length === 0)) delete out[k];
  }

  // Your defaults
  out.contact_email_status_v2 ??= ["verified","likely_to_engage","unverified","new_data_available","unavailable","user_managed"];
  out.extrapolated_email_confidence ??= ["under70","70to85","over85"];

  return { payload: out, unknown };
}

const results = $input.all().map(item => {
  const apolloUrl =
    item.json.SetApolloURL ||
    item.json.apollo_url ||
    item.json.url;

  if (!apolloUrl || typeof apolloUrl !== "string") {
    return {
      json: {
        error: "Missing Apollo URL. Expect `SetApolloURL` (or `apollo_url`/`url`) on each input item.",
        people_search_payload: null,
        unmapped_params: []
      }
    };
  }

  const { payload, unknown } = parseApolloPeopleUrl(apolloUrl);

  // If caller provided a "leads" number on the item, use it as per_page
  if (item.json.leads && Number.isFinite(Number(item.json.leads))) {
    payload.per_page = Number(item.json.leads);
  }

  return { json: { people_search_payload: payload, unmapped_params: unknown } };
});

return results;
