/**
 * PRE-PROCESSING: Verifica e prepara URLs de imagens
 * Evita download desnecess√°rio e context overflow
 */

const item = $input.item.json;
const imagePrompt = item.image_prompt;
const additionalImageUrls = item.additional_image_urls || '';

// Parse URLs
const urls = additionalImageUrls
  .split(',')
  .map(url => url.trim())
  .filter(url => url.length > 0 && url.startsWith('http'));

console.log('=== IMAGE PROCESSING ===');
console.log('Image URLs found:', urls.length);
console.log('URLs:', urls);

// IMPORTANTE: N√£o fazer download aqui!
// Vamos enviar as URLs diretamente para a API

return {
  json: {
    image_prompt: imagePrompt,
    image_urls: urls,  // Array de URLs
    image_urls_string: urls.join(','),  // String para backup
    image_count: urls.length,
    processing_mode: 'url_only'  // Flag para o pr√≥ximo node
  }
};

/**
 * COMPRESS√ÉO DE IMAGENS
 * Download e compress√£o para evitar context overflow
 * Usa apenas quando ABSOLUTAMENTE necess√°rio
 */

const item = $input.item.json;
const urls = item.image_urls || [];

// Limite de tamanho: 500KB por imagem ap√≥s compress√£o
const MAX_SIZE_KB = 500;
const MAX_DIMENSION = 1024;  // pixels
const JPEG_QUALITY = 70;

const processedImages = [];

for (const url of urls) {
  try {
    console.log(`Downloading: ${url}`);

    // Download da imagem
    const response = await $http.request({
      method: 'GET',
      url: url,
      encoding: 'arraybuffer'
    });

    // Usa sharp para comprimir (se dispon√≠vel)
    let imageData;
    try {
      const sharp = require('sharp');

      imageData = await sharp(response.data)
        .resize(MAX_DIMENSION, MAX_DIMENSION, {
          fit: 'inside',
          withoutEnlargement: true
        })
        .jpeg({ quality: JPEG_QUALITY })
        .toBuffer();

      console.log(`Compressed: ${url} -> ${(imageData.length / 1024).toFixed(2)} KB`);
    } catch (sharpError) {
      // Fallback: usar imagem original (menor confi√°vel)
      console.warn('Sharp not available, using original:', sharpError.message);
      imageData = response.data;
    }

    // Converter para base64
    const base64 = Buffer.from(imageData).toString('base64');
    const sizeKB = base64.length / 1024;

    if (sizeKB > MAX_SIZE_KB) {
      console.warn(`Image too large after compression: ${sizeKB.toFixed(2)} KB`);
      // Pular esta imagem se ainda muito grande
      continue;
    }

    processedImages.push({
      url: url,
      data: `data:image/jpeg;base64,${base64}`,
      size_kb: sizeKB.toFixed(2)
    });

  } catch (error) {
    console.error(`Failed to process image ${url}:`, error.message);
    // Continuar com as outras imagens
  }
}

console.log(`Processed ${processedImages.length}/${urls.length} images`);

return {
  json: {
    ...item,
    processed_images: processedImages,
    total_size_kb: processedImages.reduce((sum, img) => sum + parseFloat(img.size_kb), 0).toFixed(2)
  }
};

