// Formatar resposta
const cliente = $json;

if (!cliente.id) {
  return {
    json: {
      encontrado: false,
      mensagem: 'Cliente não encontrado no sistema. Será necessário coletar todos os dados.',
      dados_faltantes: [
        'nome', 'documento', 'nacionalidade', 'profissao', 'estado_civil',
        'data_nascimento', 'endereco', 'cep', 'cidade', 'estado', 'email'
      ]
    }
  };
}

const formatarData = (dataStr) => {
  if (!dataStr) return null;
  const data = new Date(dataStr);
  return data.toLocaleDateString('pt-BR');
};

// Identificar dados faltantes
const dadosFaltantes = [];
if (!cliente.nome) dadosFaltantes.push('nome');
if (!cliente.documento) dadosFaltantes.push('documento (CPF/CNPJ)');
if (!cliente.nacionalidade) dadosFaltantes.push('nacionalidade');
if (!cliente.profissao) dadosFaltantes.push('profissao');
if (!cliente.estado_civil) dadosFaltantes.push('estado_civil');
if (!cliente.data_nascimento) dadosFaltantes.push('data_nascimento');
if (!cliente.endereco) dadosFaltantes.push('endereco');
if (!cliente.cep) dadosFaltantes.push('cep');
if (!cliente.cidade) dadosFaltantes.push('cidade');
if (!cliente.estado) dadosFaltantes.push('estado');
if (!cliente.email) dadosFaltantes.push('email');

return {
  json: {
    encontrado: true,
    cliente_id: cliente.id,
    dados_existentes: {
      nome: cliente.nome || null,
      documento: cliente.documento || null,
      tipo: cliente.tipo === 'pf' ? 'Pessoa Física' : 'Pessoa Jurídica',
      razao_social: cliente.razao_social || null,
      nacionalidade: cliente.nacionalidade || null,
      profissao: cliente.profissao || null,
      estado_civil: cliente.estado_civil || null,
      data_nascimento: formatarData(cliente.data_nascimento),
      telefone: cliente.telefone || null,
      email: cliente.email || null,
      endereco: cliente.endereco || null,
      cep: cliente.cep || null,
      cidade: cliente.cidade || null,
      estado: cliente.estado || null
    },
    dados_faltantes: dadosFaltantes,
    cadastro_completo: dadosFaltantes.length === 0
  }
};
