// Formatar resposta
const items = $input.all();

if (items.length === 0 || (items.length === 1 && Object.keys(items[0].json).length === 0)) {
  return {
    json: {
      encontrado: false,
      message: 'Nenhum cliente ou fornecedor encontrado com os critérios informados',
      clientes: []
    }
  };
}

const clientes = items.map(item => {
  const c = item.json;
  return {
    id: c.id,
    nome: c.nome,
    tipo: c.tipo === 'pessoa_fisica' ? 'PF' : 'PJ',
    documento: c.documento || 'Não informado',
    telefone: c.telefone || 'Não informado',
    email: c.email || 'Não informado',
    endereco: c.endereco || 'Não informado'
  };
});

return {
  json: {
    encontrado: true,
    quantidade: clientes.length,
    clientes
  }
};
