// Consolidar alertas de contas a pagar
const config = $('Config GHL').first().json;
const resultados = $input.all();

// Organizar por tipo
const dados = {
  atrasado: { items: [], quantidade: 0, total: 0 },
  hoje: { items: [], quantidade: 0, total: 0 },
  em_3_dias: { items: [], quantidade: 0, total: 0 }
};

resultados.forEach(r => {
  const tipo = r.json.tipo_alerta;
  if (dados[tipo]) {
    dados[tipo].items = r.json.items || [];
    dados[tipo].quantidade = parseInt(r.json.quantidade) || 0;
    dados[tipo].total = parseFloat(r.json.total) || 0;
  }
});

// Se não há nada, retorna skip
const totalGeral = dados.atrasado.total + dados.hoje.total + dados.em_3_dias.total;
if (totalGeral === 0) {
  return { json: { skip: true } };
}

// Função para formatar valor
const formatarValor = (valor) => new Intl.NumberFormat('pt-BR', {
  style: 'currency',
  currency: 'BRL'
}).format(valor || 0);

// Montar mensagem
let mensagem = `*ALERTA DE CONTAS A PAGAR*\n`;
mensagem += `${new Date().toLocaleDateString('pt-BR')}\n\n`;

// Atrasados
if (dados.atrasado.quantidade > 0) {
  mensagem += `*ATRASADOS* (${dados.atrasado.quantidade})\n`;
  dados.atrasado.items.slice(0, 5).forEach(d => {
    mensagem += `- ${d.descricao}: ${formatarValor(d.valor)} (${d.dias_atraso}d atraso)\n`;
  });
  if (dados.atrasado.quantidade > 5) mensagem += `  ... e mais ${dados.atrasado.quantidade - 5}\n`;
  mensagem += `Total: *${formatarValor(dados.atrasado.total)}*\n\n`;
}

// Hoje
if (dados.hoje.quantidade > 0) {
  mensagem += `*VENCE HOJE* (${dados.hoje.quantidade})\n`;
  dados.hoje.items.forEach(d => {
    mensagem += `- ${d.descricao}: ${formatarValor(d.valor)}\n`;
  });
  mensagem += `Total: *${formatarValor(dados.hoje.total)}*\n\n`;
}

// Em 3 dias
if (dados.em_3_dias.quantidade > 0) {
  mensagem += `*VENCE EM 3 DIAS* (${dados.em_3_dias.quantidade})\n`;
  dados.em_3_dias.items.forEach(d => {
    mensagem += `- ${d.descricao}: ${formatarValor(d.valor)}\n`;
  });
  mensagem += `Total: *${formatarValor(dados.em_3_dias.total)}*\n\n`;
}

mensagem += `---\n*TOTAL PENDENTE: ${formatarValor(totalGeral)}*`;

return {
  json: {
    mensagem,
    admin_contact_id: config.admin_contact_id,
    ghl_api_key: config.ghl_api_key,
    total_geral: totalGeral
  }
};
