const allItems = $input.all();

const gpt4oItem = allItems.find(i => i.json.gpt4o_scores !== undefined);
const gpt4oData = gpt4oItem ? gpt4oItem.json : {};

const geminiItem = allItems.find(i => i.json.gemini_scores !== undefined);
const geminiData = geminiItem ? geminiItem.json : {};

const gptScores = gpt4oData.gpt4o_scores || {};
const gemScores = geminiData.gemini_scores || {};

const avgScore = (gpt, gem) => {
  const hasGpt = typeof gpt === 'number';
  const hasGem = typeof gem === 'number';
  if (hasGpt && hasGem) return Math.round((gpt + gem) / 2);
  if (hasGpt) return gpt;
  if (hasGem) return gem;
  return 0;
};

const finalScores = {
  tool_selection: avgScore(gptScores.tool_selection, gemScores.tool_selection),
  confirmation_protocol: avgScore(gptScores.confirmation_protocol, gemScores.confirmation_protocol),
  brevity: avgScore(gptScores.brevity, gemScores.brevity),
  professionalism: avgScore(gptScores.professionalism, gemScores.professionalism),
  data_accuracy: avgScore(gptScores.data_accuracy, gemScores.data_accuracy),
  rule_adherence: avgScore(gptScores.rule_adherence, gemScores.rule_adherence),
  context_usage: avgScore(gptScores.context_usage, gemScores.context_usage),
  format_compliance: avgScore(gptScores.format_compliance, gemScores.format_compliance)
};

const weights = {
  tool_selection: 0.20,
  confirmation_protocol: 0.15,
  brevity: 0.10,
  professionalism: 0.10,
  data_accuracy: 0.20,
  rule_adherence: 0.10,
  context_usage: 0.10,
  format_compliance: 0.05
};

let weightedSum = 0;
for (const [key, weight] of Object.entries(weights)) {
  weightedSum += (finalScores[key] || 0) * weight;
}
const finalScore = Math.round(weightedSum * 100) / 100;

let grade;
if (finalScore >= 90) grade = 'A';
else if (finalScore >= 75) grade = 'B';
else if (finalScore >= 60) grade = 'C';
else if (finalScore >= 40) grade = 'D';
else grade = 'F';

let status;
