function buscarCalendario(inputId) {
  try {
    // Converter ID para string para compara√ß√£o
    const id = String(inputId).trim();

    // Buscar respons√°vel pelo ID
    const responsavel = CALENDARIO_DATA.responsaveis[id];

    if (!responsavel) {
      return {
        erro: true,
        mensagem: `Calend√°rio n√£o encontrado para o ID: ${id}`,
        ids_disponiveis: Object.keys(CALENDARIO_DATA.responsaveis),
        timestamp: new Date().toISOString()
      };
    }

    // Construir resposta estruturada
    const resultado = {
      sucesso: true,
      timestamp: new Date().toISOString(),
      responsavel_principal: {
        nome: responsavel.nome,
        id: responsavel.id,
        carreira_id: responsavel.carreira_id,
        consultoria_id: responsavel.consultoria_id
      },
      regras_especiais: {
        tem_excecoes: !!responsavel.excecoes,
        excecoes: responsavel.excecoes,
        backup_disponivel: !!responsavel.backup,
        backup: responsavel.backup,
        event_especial: responsavel.event_especial || null
      },
      funcionarios_associados: {
        total: responsavel.funcionarios_associados.length,
        lista: responsavel.funcionarios_associados.map(func => ({
          nome: func.nome,
          id: func.id || null,
          carreira_id: func.carreira_id || null,
          consultoria_id: func.consultoria_id || null,
          especialidade: func.especialidade,
          tipo: func.tipo
        }))
      },
      resumo_legivel: gerarResumoLegivel(responsavel)
    };

    return resultado;

  } catch (error) {
    return {
      erro: true,
      mensagem: `Erro interno: ${error.message}`,
      timestamp: new Date().toISOString()
    };
  }
}

// ===== FUN√á√ÉO AUXILIAR =====
function gerarResumoLegivel(responsavel) {
  let resumo = [`üìÖ **Respons√°vel:** ${responsavel.nome} (ID: ${responsavel.id})`];

  // Adicionar IDs dos calend√°rios do respons√°vel principal
  resumo.push(`üìã **Calend√°rios:**`);
  resumo.push(`  ‚Ä¢ Carreira: ${responsavel.carreira_id}`);
  if (responsavel.consultoria_id) {
    resumo.push(`  ‚Ä¢ Consultoria: ${responsavel.consultoria_id}`);
  }

  // Adicionar informa√ß√µes sobre funcion√°rios associados
  if (responsavel.funcionarios_associados.length > 0) {
    resumo.push(`\nüë• **Equipe Associada:**`);
    responsavel.funcionarios_associados.forEach(func => {
      resumo.push(`  ‚Ä¢ **${func.nome}**`);
      if (func.carreira_id) {
        resumo.push(`    - Carreira: ${func.carreira_id}`);
      }
      if (func.consultoria_id) {
        resumo.push(`    - Consultoria: ${func.consultoria_id}`);
      }
      resumo.push(`    - Especialidade: ${func.especialidade}`);
    });
  }

  // Adicionar exce√ß√µes se existirem
  if (responsavel.excecoes) {
    resumo.push(`\n‚ö†Ô∏è **Regras Especiais:**`);
    resumo.push(`  ${responsavel.excecoes}`);
  }

  // Adicionar informa√ß√µes de backup
  if (responsavel.backup) {
    resumo.push(`\nüîÑ **Backup/Substitui√ß√£o:** ${responsavel.backup}`);
  }

  return resumo.join('\n');
}

