function formatarTelefoneBrasil(numero) {
  let limpo = numero.replace(/\D/g, '');

  if (limpo.startsWith('55')) {
    limpo = limpo.substring(2);
  }

  if (limpo.length >= 10) {
    const ddd = parseInt(limpo.substring(0, 2));

    if (dddsBrasil.includes(ddd)) {
      // Celular com 9 d√≠gitos
      if (limpo.length === 11 && limpo[2] === '9') {
        return `+55${limpo}`;
      }
      // Celular sem o 9 (adiciona)
      else if (limpo.length === 10 && limpo[2] >= '6') {
        return `+55${limpo.substring(0, 2)}9${limpo.substring(2)}`;
      }
      // Fixo
      else if (limpo.length === 10) {
        return `+55${limpo}`;
      }
    }
  }

  return null;
}

function formatarTelefoneEUA(numero) {
  let limpo = numero.replace(/\D/g, '');

  if (limpo.startsWith('1')) {
    limpo = limpo.substring(1);
  }

  if (limpo.length === 10) {
    const areaCode = parseInt(limpo.substring(0, 3));
    if (areaCodesEUA.includes(areaCode)) {
      return `+1${limpo}`;
    }
  }

  return null;
}

function detectarEFormatarTelefone(numero) {
  numero = numero.trim();
  let limpo = numero.replace(/\D/g, '');

  // J√° tem c√≥digo do pa√≠s
  if (numero.startsWith('+55') || limpo.startsWith('55')) {
    return formatarTelefoneBrasil(numero);
  }
  if (numero.startsWith('+1') || (limpo.startsWith('1') && limpo.length === 11)) {
    return formatarTelefoneEUA(numero);
  }

  // 10 d√≠gitos: PRIORIZA EUA
  if (limpo.length === 10) {
    const areaCode = parseInt(limpo.substring(0, 3));

    // Se √°rea code americano v√°lido, √© EUA
    if (areaCodesEUA.includes(areaCode)) {
      return formatarTelefoneEUA(numero);
    }

    // Sen√£o, tenta Brasil (fixo)
    return formatarTelefoneBrasil(numero);
  }

  // 11 d√≠gitos: Provavelmente Brasil
  if (limpo.length === 11) {
    const possivel = formatarTelefoneBrasil(numero);
    if (possivel) return possivel;

    // Se come√ßar com 1, pode ser EUA com c√≥digo
