    if (formatado) {
      telefonesFormatados.push(formatado);
    }
  });

  telefonesFormatados = [...new Set(telefonesFormatados)];

  // Busca emails
  const emails = mensagem.match(emailPattern) || [];
  const emailsUnicos = [...new Set(emails)];

  if (telefonesFormatados.length > 0 || emailsUnicos.length > 0) {
    return {
