// Prepare prompt for agent
const item = $input.first().json;

const prompt = `Transforme este insight em uma entrada de Knowledge Base:

Tipo: ${item.insight_type || 'unknown'}
Conteúdo: ${item.content || ''}
Confiança: ${item.confidence_score || 0}
Contexto: ${JSON.stringify(item.metadata || {})}

Responda APENAS com JSON válido, sem markdown, sem explicações:
{
  "category": "faq|solution|process|best_practice",
  "title": "Título claro",
  "content": "Conteúdo detalhado...",
  "tags": ["tag1", "tag2"],
  "priority": 3,
  "should_create": true,
  "reason": "Motivo da decisão"
}`;

return [{
  json: {
    prompt: prompt,
    insight_id: item.id,
    original_content: item.content
  }
}];

// Parse the AI response
const aiResponse = $input.first().json;
const preparedData = $('Prepare Agent Prompt').first().json;

let parsed;
try {
  // Try to get output from agent
  const content = aiResponse.output || aiResponse.text || aiResponse.content || JSON.stringify(aiResponse);

  // Remove markdown code blocks if present
  let cleanContent = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  // Find JSON in the response
  const jsonMatch = cleanContent.match(/\{[\s\S]*\}/);
  parsed = jsonMatch ? JSON.parse(jsonMatch[0]) : null;
} catch (e) {
  parsed = null;
}

if (!parsed || !parsed.should_create) {
  return [{
    json: {
      skip: true,
      insight_id: preparedData.insight_id,
      reason: parsed?.reason || 'Could not parse or not relevant'
    }
  }];
}

return [{
  json: {
    skip: false,
    insight_id: preparedData.insight_id,
    knowledge_entry: {
      category: parsed.category,
      title: parsed.title,
      content: parsed.content,
      tags: parsed.tags || [],
      priority: parsed.priority || 3,
      source: 'sentinel_ai',
      source_insight_id: preparedData.insight_id,
      status: 'published'
    }
  }
}];
